SELECT 'To' AS RECIPIENTTYPE , USR.EML AS ADDRESS , CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END AS NAME , USR.USR_ID AS ID FROM APVL_RDG APVL_RDG INNER JOIN USR USR ON USR.TEN_ID = APVL_RDG.TEN_ID AND USR.USR_ID = APVL_RDG.USR_ID WHERE APVL_RDG.TEN_ID = #{g.tenant} AND APVL_RDG.APVL_UUID = #{p.apvl_uuid} AND APVL_RDG.RDG_TYP_CCD = #{p.rdg_typ_ccd}
AND APVL_RDG.USR_ID IN
#{newRefUsrId}

SELECT 'To' AS RECIPIENTTYPE , USR.EML AS ADDRESS , CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END AS NAME , USR.USR_ID AS ID FROM APVL INNER JOIN APVLLN_APVR_PRGS_STS ON APVL.TEN_ID = APVLLN_APVR_PRGS_STS.TEN_ID AND APVL.APVL_UUID = APVLLN_APVR_PRGS_STS.APVL_UUID INNER JOIN USR ON USR.TEN_ID = APVLLN_APVR_PRGS_STS.TEN_ID AND USR.USR_ID = APVLLN_APVR_PRGS_STS.USR_ID WHERE APVL.TEN_ID = #{g.tenant} AND APVL.APVL_UUID = #{p.apvl_uuid} AND APVLLN_APVR_PRGS_STS.APVLLN_SORT = #{p.next_apvlln_sort}
AND APVLLN_APVR_PRGS_STS.APVR_TYP_CCD = 'APVL'
AND APVLLN_APVR_PRGS_STS.APVR_TYP_CCD IN ('AG', 'PARLL_AG')
