mapper.select:
/* arbitrary-datasrc.selectList_Code: 공통코드 목록 - 예시 */
SELECT DT.TEN_ID
     , DT.CCD
     , DT.DTLCD AS DATA
     , DL.DTLCD_NM AS LABEL
     , DT.DTLCD_SORT
  FROM DTLCD DT
 INNER JOIN DTLCD_MULTLANG DL
    ON DT.TEN_ID = DL.TEN_ID
   AND DT.CCD = DL.CCD
   AND DT.DTLCD = DL.DTLCD
   AND DL.LANG_CCD = #{g.locale}
 WHERE DT.TEN_ID = #{g.tenant}
   AND DT.USE_YN = 'Y'
   AND DT.STS != 'D'

   AND DT.CCD = #{p.ccd}



   AND DT.DTLCD = #{DTLCD}



   AND DL.DTLCD_NM = #{dtlcd_nm}

 ORDER
    BY DT.DTLCD_SORT ASC

/* arbitrary-datasrc.selectList_Plant: 플랜트 목록 - 예시 */ SELECT 'plant_001' AS plant_id , 'plant name 1' AS plant_nm FROM DUAL
UNION ALL SELECT 'plant_002' AS plant_id , 'plant name 2' AS plant_nm FROM DUAL
UNION ALL SELECT 'plant_003' AS plant_id , 'plant name 3' AS plant_nm FROM DUAL

/* arbitrary-datasrc.selectList_Item: 자재 목록 - 예시 */ SELECT 'item_001' AS item_id , 'item name 1' AS item_nm FROM DUAL
UNION ALL SELECT 'item_002' AS item_id , 'item name 2' AS item_nm FROM DUAL
UNION ALL SELECT 'item_003' AS item_id , 'item name 3' AS item_nm FROM DUAL

/* arbitrary-datasrc.selectList_Dept: 부서 목록 - 예시 */ SELECT DEPT.TEN_ID , DEPT.DEPT_CD , CASE WHEN 'en_US' = #{g.locale} THEN DEPT.DEPT_NM_EN ELSE DEPT.DEPT_NM END AS DISP_DEPT_NM , DEPT.DEPT_NM , DEPT.DEPT_NM_EN , DEPT.PAR_DEPT_CD , DEPT.USE_YN , DEPT.RMK , DEPT.STS , DEPT.REGR_ID , DEPT.REG_DTTM , DEPT.MODR_ID , DEPT.MOD_DTTM , 1 AS RECURSIVE_LEVEL , DEPT.DEPT_CD AS TREE FROM DEPT DEPT INNER JOIN LOGIC_ORG_DEPT_MAPG ODM ON DEPT.TEN_ID = ODM.TEN_ID AND DEPT.DEPT_CD = ODM.DEPT_CD INNER JOIN LOGIC_ORG LOGIC_ORG ON DEPT.TEN_ID = LOGIC_ORG.TEN_ID AND ODM.LOGIC_ORG_TYP_CCD = LOGIC_ORG.LOGIC_ORG_TYP_CCD AND ODM.LOGIC_ORG_CD = LOGIC_ORG.LOGIC_ORG_CD WHERE DEPT.TEN_ID = #{g.tenant}
AND LOGIC_ORG.LOGIC_ORG_CD = #{p.logic_org_cd}
AND DEPT.DEPT_CD LIKE UPPER(#{deptCdPattern})
AND (UPPER(DEPT.DEPT_NM_EN) LIKE UPPER(#{deptNmPattern}) OR UPPER(DEPT.DEPT_NM) LIKE UPPER(#{deptNmPattern}))
AND DEPT.STS != 'D' ORDER BY DEPT.DEPT_CD

/* arbitrary-datasrc.selectList_OORG */ SELECT A.TEN_ID ,A.OORG_CD , CONCAT(CONCAT(CONCAT('[', OUNIT.OUNIT_NM), '] '), C.LOGIC_ORG_NM) AS OORG_NM ,B.PARNODE_OORG_CD ,B.OUNIT_CD ,B.LOGIC_ORG_TYP_CCD ,B.LOGIC_ORG_CD ,B.PARNODE_LOGIC_ORG_CD ,B.CHDNODE_YN ,A.USE_YN ,B.LOGIC_ORG_CD AS CO_CD ,B.USE_YN AS ORG_USE_YN , CASE WHEN 'en_US' = #{g.locale} THEN C.LOGIC_ORG_NM_EN ELSE C.LOGIC_ORG_NM END AS LOGIC_ORG_NM FROM OORG_USR A INNER JOIN OORG B ON B.TEN_ID = A.TEN_ID AND B.OORG_CD = A.OORG_CD INNER JOIN LOGIC_ORG C ON C.TEN_ID = B.TEN_ID AND C.LOGIC_ORG_TYP_CCD = B.LOGIC_ORG_TYP_CCD AND C.LOGIC_ORG_CD = B.LOGIC_ORG_CD INNER JOIN DTLCD_MULTLANG DTLML ON DTLML.TEN_ID = A.TEN_ID AND DTLML.CCD = 'C079' AND DTLML.LANG_CCD = 'ko_KR' AND B.LOGIC_ORG_TYP_CCD = DTLML.DTLCD INNER JOIN OUNIT OUNIT ON B.TEN_ID = OUNIT.TEN_ID AND B.OUNIT_CD = OUNIT.OUNIT_CD WHERE A.TEN_ID = #{g.tenant} AND A.USR_ID = #{g.username} --AND A.USE_YN = 'Y' /* 운영조직-사용자 use_yn 사용안함(null). insert/delete */
AND B.OUNIT_CD = #{p.ccd}
AND B.USE_YN = 'Y' AND B.STS != 'D' AND C.USE_YN = 'Y' AND C.STS != 'D' ORDER BY A.OORG_CD

/* arbitrary-datasrc.selectList_EvalType */ SELECT TEN_ID /* 시스템아이디 */ ,EVAL_TYP_ID /* 평가업무구분아이디 */ ,OORG_CD /* SRM 운영단위 */ ,EVAL_TYP_CD /* 평가업무구분코드 */ ,EVAL_KIND_CD /* 평가종류코드 */ ,EVAL_KIND_NM /* 평가종류명 */ ,EVAL_CYCL_CD /* 평가 주기 코드 */ FROM ESREVTP EVTP /* EVTP : 평가업무구분 */ WHERE EVTP.STS != 'D' AND EVTP.Use_Yn = 'Y' AND EVTP.TEN_ID = #{g.tenant}
AND EVTP.OORG_CD = {p.oorg_cd}
ORDER BY EVTP.SORT_ORD DESC
