mapper.insert:
/* roleUrl.insertFuncUrl: 역할url 등록 */
INSERT
  INTO MENU_ACT_URL (
       TEN_ID
     , ACT_CD
     , MENU_CD
     , MENU_ACT_URL
     , REGR_ID
     , REG_DTTM
) VALUES (
       #{g.tenant}
     , #{p.act_cd}
     , #{p.menu_cd}
     , #{p.menu_act_url}
     , #{g.username}
     , #{g.now}
)

mapper.select:
/* roleUrl.getCountFuncUrl: 역할url 카운트 */
SELECT COUNT(*)
  FROM MENU_ACT_URL
 WHERE TEN_ID = #{g.tenant}
   AND MENU_CD = #{p.menu_cd}
   AND MENU_ACT_URL = #{p.menu_act_url}

mapper.update:
   		UPDATE MENU_ACT_URL

    	ACT_CD = #{p.act_cd}


		TEN_ID = #{g.tenant}
		AND MENU_CD = #{p.menu_cd}
		AND MENU_ACT_URL = #{p.menu_act_url}

mapper.delete:
/* roleUrl.getCountFuncUrl: 호출패턴 역할 전체 삭제 */
    DELETE FROM MENU_ACT_URL
     WHERE TEN_ID  = #{g.tenant}
       AND MENU_CD = #{p.menu_cd}

/* auth-url.deleteFuncUrl: 호출패턴 역할 삭제 */
    DELETE FROM MENU_ACT_URL
     WHERE TEN_ID  = #{g.tenant}
       AND MENU_CD = #{p.menu_cd}
       AND MENU_ACT_URL = #{p.menu_act_url}
