mapper.select:
/* boardAdmin.findBoardAdminList */ SELECT TEN_ID /*시스템 아이디 */ ,BBD_UUID /*게시판_아이디 */ ,BBD_NM /*게시판_이름 */ ,BBD_DSPY_NM /*게시판_표시_명 */ ,BBD_PAGE_LIST_CNT /*게시판_목록_수 */ ,BBD_PAGE_CNT /*게시판_페이지_수 */ ,PST_TIT_LEN /*게시판_제목_길이 */ ,PST_NO_SHOW_YN /*게시물_번호_보이기_여부 */ ,REG_DTTM_SHOW_YN /*등록_일시_보이기_여부 */ ,CRTR_SHOW_YN /*등록자_명_보이기_여부 */ ,VCNT_SHOW_YN /*조회_수_보이기_여부 */ ,ATH_SHOW_YN /*첨부_보이기_여부 */ ,CAT_SHOW_YN /*카테고리_보이기_여부 */ ,PSTG_PD_USE_YN /*기간_사용_여부 */ ,CMNT_USE_YN /*댓글_사용_여부 */ ,CAT_USE_YN /*카테고리_사용_여부 */ ,SECTXT_USE_YN /*비밀글_사용_여부 */ ,ATH_USE_YN /*첨부_사용_여부 */ ,BBD_RE_USE_YN /*답글_사용_여부 */ ,EDTR_TYP /*에디터_유형 */ ,REG_DTTM /*등록_일시 */ ,MOD_DTTM /*수정_일시 */ ,REGR_ID /*등록자_아이디 */ ,MODR_ID /*수정자_아이디 */ ,CO_BBD_USE_YN /* 회사(법인)별 게시판 사용 여부 */ FROM BBD_SETUP WHERE TEN_ID = #{g.tenant}
AND BBD_NM LIKE #{boardNmPattern}
AND BBD_DSPY_NM LIKE #{boardDispNmPattern}
ORDER BY BBD_UUID

	/* boardAdmin.findBoardId */
           SELECT CASE WHEN BBD_UUID IS NULL THEN 1
                       ELSE BBD_UUID+1 END
             FROM (
                   SELECT MAX(CAST(BBD_UUID AS INT)) AS BBD_UUID
                     FROM BBD_SETUP
                    WHERE TEN_ID = #{g.tenant}
                  ) TMP

mapper.insert:
/* boardAdmin.insertBoardAdmin */
	INSERT INTO BBD_SETUP
	(
		 TEN_ID				/*시스템 아이디 */
		,BBD_UUID			/*게시판_아이디 */
		,BBD_NM			/*게시판_이름 */
		,PSTG_SUBJ_TYP		/*게시판_유형 */
		,BBD_DSPY_NM		/*게시판_표시_명 */
		,BBD_PAGE_LIST_CNT		/*게시판_목록_수 */
		,BBD_PAGE_CNT		/*게시판_페이지_수 */
		,PST_TIT_LEN		/*게시판_제목_길이 */
		,PST_NO_SHOW_YN	/*게시물_번호_보이기_여부 */
		,REG_DTTM_SHOW_YN		/*등록_일시_보이기_여부 */
		,CRTR_SHOW_YN		/*등록자_명_보이기_여부 */
		,VCNT_SHOW_YN	/*조회_수_보이기_여부 */
		,ATH_SHOW_YN		/*첨부_보이기_여부 */
		,CAT_SHOW_YN		/*카테고리_보이기_여부 */
		,PSTG_PD_USE_YN			/*기간_사용_여부 */
		,CMNT_USE_YN		/*댓글_사용_여부 */
		,CAT_USE_YN		/*카테고리_사용_여부 */
		,SECTXT_USE_YN		/*비밀글_사용_여부 */
		,ATH_USE_YN			/*첨부_사용_여부 */
		,BBD_RE_USE_YN		/*답글_사용_여부 */
		,EDTR_TYP			/*에디터_유형 */
		,REG_DTTM				/*등록_일시 */
		,REGR_ID				/*등록자_아이디 */
		,CO_BBD_USE_YN		/* 회사(법인)별 게시판 사용 여부 */
	)
	VALUES
	(
		 #{g.tenant}
		,#{p.bbd_uuid}
		,#{p.bbd_nm}
		,#{p.pstg_subj_typ}
		,#{p.bbd_dspy_nm}
		,#{p.bbd_page_list_cnt}
		,#{p.bbd_page_cnt}
		,#{p.pst_tit_len}
		,#{p.pst_no_show_yn}
		,#{p.reg_dttm_show_yn}
		,#{p.crtr_show_yn}
		,#{p.vcnt_show_yn}
		,#{p.ath_show_yn}
		,#{p.cat_show_yn}
		,#{p.pstg_pd_use_yn}
		,#{p.cmnt_use_yn}
		,#{p.cat_use_yn}
		,#{p.sectxt_use_yn}
		,#{p.ath_use_yn}
		,#{p.bbd_re_use_yn}
		,#{p.edtr_typ}
		,#{g.now}
		,#{g.username}
		,#{p.co_bbd_use_yn}
	)

mapper.select:
	/* boardAdmin.findBoardByBoardIdList */
           SELECT *
             FROM BBD A
        LEFT JOIN BBD_CMNT B
               ON A.TEN_ID   = B.TEN_ID
              AND A.PST_NO  = B.PST_NO
            WHERE A.TEN_ID   = #{g.tenant}
              AND A.BBD_UUID = #{p.bbd_uuid}

mapper.delete:
/* deleteBoardAuthById */
	DELETE
	  FROM BBD_ROLE
	 WHERE TEN_ID 	= #{g.tenant}
	   AND BBD_UUID = #{p.bbd_uuid}

mapper.select:
/* boardAdmin.findBoardAdminYnInfo */
	SELECT CASE WHEN COUNT(*) > 0 THEN 'Y' ELSE 'N' END AS admin_yn
	  FROM BBD_MGR
	 WHERE TEN_ID 	= #{g.tenant}
	   AND BBD_UUID = #{p.bbd_uuid}
	   AND USR_ID	= #{g.username}

mapper.insert:
/* boardAdmin.insertAdminUser */
	INSERT INTO BBD_MGR
	(
		 TEN_ID 	/*시스템 아이디 */
		,BBD_UUID 	/*게시판_아이디 */
		,CO_CD	/*회사 코드 */
		,USR_ID 	/*사용자_아이디 */
		,REG_DTTM 	/*등록 일시 */
		,REGR_ID 	/*등록자 아이디 */
	)
	VALUES
	(
		 #{g.tenant}
		,#{p.bbd_uuid}
		,#{p.co_cd}
		,#{p.usr_id}
		,#{g.now}
		,#{g.username}
	)

mapper.select:
	/* boardAdmin.findInfoBoardRole */
           SELECT MAX(B.WRTG_PERM_YN)  AS WRTG_PERM_YN
                 ,MAX(B.SHOW_YN) AS SHOW_YN
             FROM ROLE_USR A
       INNER JOIN BBD_ROLE B
               ON A.TEN_ID   = B.TEN_ID
              AND A.ROLE_CD  = B.ROLE_CD
            WHERE A.TEN_ID   = #{g.tenant}
              AND A.USR_ID   = #{g.username}
              AND B.BBD_UUID = #{p.bbd_uuid}

mapper.insert:
/* boardAdmin.insertBoardAuth */
	INSERT INTO BBD_ROLE
	(
		 TEN_ID		/*시스템 아이디 */
		,BBD_UUID	/*게시판_아이디 */
		,ROLE_CD	/*롤 코드 */
		,WRTG_PERM_YN		/*쓰기 여부 */
		,SHOW_YN	/*조회 여부 */
		,REG_DTTM		/*등록 일시 */
		,REGR_ID		/*등록자 아이디 */
	)
	VALUES
	(
		 #{g.tenant}
		,#{p.bbd_uuid}
		,#{p.role_cd}
		,#{p.wrtg_perm_yn}
		,#{p.show_yn}
		,#{g.now}
		,#{g.username}
	)

mapper.delete:
/* boardAdmin.deleteBoardAuth */
	DELETE
	  FROM BBD_ROLE
	 WHERE TEN_ID 	= #{g.tenant}
	   AND BBD_UUID = #{p.bbd_uuid}
	   AND ROLE_CD	= #{p.role_cd}

mapper.select:
/* boardAdmin.findBoardCompany : 게시판 사용 회사(법인) 조회*/
SELECT BC.CO_CD
	 , (SELECT CASE WHEN 'en_US' = #{g.locale} THEN LOGIC_ORG_NM_EN
					ELSE LOGIC_ORG_NM
					END AS COMP_NM
		 FROM LOGIC_ORG
		WHERE TEN_ID = #{g.tenant}
		 AND LOGIC_ORG_TYP_CCD = 'COMPANY'
		 AND LOGIC_ORG_CD = BC.CO_CD
		 AND USE_YN = 'Y'
		 AND STS != 'D') AS COMP_NM
  FROM BBD_CO BC
WHERE BC.TEN_ID = #{g.tenant}
  AND BC.BBD_UUID = #{p.bbd_uuid}
  AND BC.STS != 'D'

  AND BC.CO_CD = #{p.co_cd}

mapper.insert:
/* boardAdmin.insertBoardCompany : 게시판 사용 회사(법인) 등록 */
INSERT INTO BBD_CO
(
 	TEN_ID
	, BBD_UUID
	, CO_CD
	, RMK
	, REGR_ID
	, MODR_ID
	, MOD_DTTM
)
VALUES
(
	#{g.tenant}
	, #{p.bbd_uuid}
	, #{p.co_cd}
	, #{p.rmk}
	, #{g.username}
	, #{g.username}
	, #{g.now}
)

mapper.delete:
/* boardAdmin.insertBoardCompany : 게시판 사용 회사(법인) 삭제 */
DELETE
 FROM BBD_CO
WHERE TEN_ID = #{g.tenant}
  AND BBD_UUID = #{p.bbd_uuid}
  AND CO_CD = #{p.co_cd}

mapper.update:
/* boardAdmin.updateBoardCompany : 게시판 사용 회사(법인) 수정 */
UPDATE BBD_CO
   SET STS = #{p.sts}
     , MODR_ID = #{g.username}
	 , MOD_DTTM = #{g.now}
WHERE TEN_ID = #{g.tenant}
  AND BBD_UUID = #{p.bbd_uuid}

  AND CO_CD = #{p.co_cd}
