mapper.select:
/* common-code.findListGroupCode */ SELECT TEN_ID /*시스템 아이디*/ ,CCD /*그룹 코드*/ ,CCD_TYP /*코드 유형*/ ,CCD_NM /*그룹 코드 명*/ ,CCD_NM_EN /*그룹 코드 영문 명*/ ,USE_YN /*사용 여부*/ ,RMK /*비고*/ ,STS /*상태*/ ,REGR_ID /*등록자 아이디*/ ,REG_DTTM /*등록 일시*/ ,MODR_ID /*수정자 아이디*/ ,MOD_DTTM /*수정 일시*/ FROM CCD
AND TEN_ID = #{g.tenant}
AND UPPER(CCD) LIKE UPPER(#{grpCdPattern})
AND CCD_TYP = #{p.ccd_typ}
AND UPPER(CCD_NM) LIKE UPPER(#{grpCdNmPattern})
ORDER BY CCD_TYP ,CCD

mapper.insert:
	/* common-code.insertGroupCodeInfo */
       INSERT INTO CCD

	            STS            /*상태*/
	           ,TEN_ID         /*시스템 아이디*/
	           ,REGR_ID         /*등록자 아이디*/
	           ,REG_DTTM         /*등록 일시*/
	           ,CCD         /*그룹 코드*/
	           ,CCD_TYP         /*코드 유형*/
	           ,CCD_NM      /*그룹 코드 명*/
	           ,CCD_NM_EN   /*그룹 코드 영문 명*/
	           ,USE_YN         /*사용 여부*/
	           ,RMK            /*비고*/


				'C'
				,#{g.tenant}
				,#{g.username}
				,#{g.now}
				,#{p.ccd}
				,#{p.ccd_typ}
	        	,#{p.ccd_nm}
	        	,#{p.ccd_nm_en}
		        ,#{p.use_yn}
		        ,#{p.rmk}

mapper.update:
/* common-code.updateGroupCodeInfo */
	  UPDATE CCD

			 MODR_ID 		= #{g.username}		/*수정자 아이디*/
			,MOD_DTTM 		= #{g.now}			/*수정 일시*/
	   		,CCD_NM 		= #{p.ccd_nm} 	/*그룹 코드 명*/
	 		,CCD_NM_EN 	= #{p.ccd_nm_en}	/*그룹 코드 영문 명*/
	 		,USE_YN 		= #{p.use_yn}		/*사용 여부*/

	   WHERE TEN_ID = #{g.tenant}
	     AND CCD = #{p.ccd}

mapper.delete:
/* common-code.deleteGroupCodeInfo */
	DELETE 
	  FROM CCD
	 WHERE TEN_ID 	= #{g.tenant}
	   AND CCD	= #{p.ccd}

mapper.select:
/* common-code.findListGroupCodeAttribute */ SELECT TEN_ID /*시스템 아이디*/ ,CCD /*그룹 코드*/ ,CSTR_CND_CD /*속성 코드*/ ,CSTR_CND_NM /*속성 코드 명*/ ,CSTR_CND_NM_EN /*속성 코드 영문 명*/ ,CSTR_CND_SORT /*정렬 순서*/ ,USE_YN /*사용 여부*/ ,RMK /*비고*/ ,STS /*상태*/ ,REGR_ID /*등록자 아이디*/ ,REG_DTTM /*등록 일시*/ ,MODR_ID /*수정자 아이디*/ ,MOD_DTTM /*수정 일시*/ FROM CCD_CSTR_CND
AND TEN_ID = #{g.tenant}
AND CCD = #{p.ccd}
AND CSTR_CND_CD = #{p.cstr_cnd_cd}
ORDER BY CSTR_CND_SORT ,CSTR_CND_CD

mapper.insert:
	/* common-code.insertGroupCodeAttributeInfo */
		INSERT INTO CCD_CSTR_CND

				 STS			/*상태*/
        		,TEN_ID			/*시스템 아이디*/
