        		,CCD			/*그룹 코드*/
        		,CSTR_CND_CD		/*속성 코드*/
        		,CSTR_CND_NM		/*속성 코드 명*/
        		,CSTR_CND_NM_EN	/*속성 코드 영문 명*/
        		,CSTR_CND_SORT		/*정렬 순서*/
