mapper.insert:
	/* common-code.insertDefaultGroupCodeDetailName */
		INSERT INTO DTLCD_MULTLANG
			(
			TEN_ID
			,LANG_CCD
			,CCD
			,DTLCD
			,DTLCD_NM
			,RMK
			,STS
			,REGR_ID
			,REG_DTTM
			)
			SELECT
				   TEN_ID
				  ,#{p.locale}
				  ,CCD
				  ,DTLCD
				  ,DTLCD_NM
				  ,RMK
				  ,STS
				  ,'SYSTEM'
				  ,#{g.now}
			 FROM DTLCD_MULTLANG
			WHERE TEN_ID =  #{g.tenant}
			  AND LANG_CCD = #{p.defaultLocale}
			  AND CCD = #{p.ccd}
			  AND DTLCD = #{p.dtlcd}
			  AND STS != 'D'
			  AND NOT EXISTS ( SELECT 1 
                                 FROM DTLCD_MULTLANG
                                WHERE TEN_ID = #{g.tenant}
                                  AND LANG_CCD = #{p.locale}
                                  AND CCD = #{p.ccd} 
                                  AND DTLCD = #{p.dtlcd}
                                  AND STS != 'D'
                             )
