mapper.select:
/* holi.findListHd */
	SELECT HOL_UUID
		, HOL_DT
		, HOL_NM
		, HOL_TYP_CCD
		, MKCND_PRVDR_CD
	FROM HOL
	WHERE TEN_ID = #{g.tenant}

		AND HOL_TYP_CCD = #{p.hol_typ_ccd}



	    AND UPPER(HOL_NM) LIKE UPPER(#{nmPattern})


	    AND HOL_DT >= #{p.from_hd_date}



	    AND HOL_DT < #{toDatePattern}

/* holi.getCountHdByDate */
	SELECT COUNT(HOL_UUID)
	FROM HOL
	WHERE TEN_ID = #{g.tenant}
	AND HOL_DT = #{p.hol_dt}

mapper.insert:
/* holi.insertHd */
	INSERT INTO HOL

		TEN_ID
		, HOL_UUID
		, HOL_DT
		, HOL_TYP_CCD
		, HOL_NM
		, MKCND_PRVDR_CD
		, STS
		, REGR_ID
		, REG_DTTM

	VALUES

		#{g.tenant}
		, #{g.uuid}
		, #{p.hol_dt}
		, #{p.hol_typ_ccd}
		, #{p.hol_nm}
		, #{p.mkcnd_prvdr_cd}
		, 'C'
		, #{g.username}
		, #{g.now}

mapper.update:
/* holi.updateHd */
	UPDATE HOL
	SET HOL_NM = #{p.hol_nm}
		, HOL_DT = #{p.hol_dt}
		, HOL_TYP_CCD = #{p.hol_typ_ccd}
		, MKCND_PRVDR_CD = #{p.mkcnd_prvdr_cd}
	WHERE TEN_ID = #{g.tenant}
	AND HOL_UUID = #{p.hol_uuid}

mapper.delete:
/* holi.deleteHd */
	DELETE FROM HOL
	WHERE TEN_ID = #{g.tenant}
	AND HOL_UUID = #{p.hol_uuid}
