mapper.select:
/* job.findListPurchaseGroupCategory: 직무 목록 조회 */ SELECT TEN_ID ,PURC_GRP_TYP_CCD ,PURC_GRP_CD ,CO_CD , CASE WHEN 'en_US' = #{g.locale} THEN PURC_GRP_NM ELSE PURC_GRP_NM_EN END AS DISPLAY_PURC_GRP_NM , PURC_GRP_NM /* 구매그룹명 */ ,PURC_GRP_NM_EN ,USE_YN ,REGR_ID ,REG_DTTM ,MODR_ID ,MOD_DTTM FROM PURC_GRP WHERE TEN_ID = #{g.tenant} AND STS != 'D'
AND CO_CD = #{p.co_cd}
AND PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}

mapper.insert:
    /* job.saveLIstJob: 직무 저장 */
		INSERT INTO PURC_GRP (
	    	 TEN_ID
	    	,PURC_GRP_TYP_CCD
	    	,PURC_GRP_CD
	    	,CO_CD
	    	,PURC_GRP_NM
	    	,PURC_GRP_NM_EN
	    	,USE_YN
	    	,REGR_ID
	    	,REG_DTTM )
	    VALUES (
	    	#{g.tenant}
	    	,#{p.purc_grp_typ_ccd}
	    	,#{p.purc_grp_cd}
	    	,#{p.co_cd}
	    	,#{p.purc_grp_nm}	    	
	    	,#{p.purc_grp_nm_en}
	    	,#{p.use_yn}
	    	,#{g.username}
	    	,#{g.now}	    		    	
	    )

mapper.update:

    /* job.saveLIstJob: 직무 수정 */
		UPDATE PURC_GRP SET
	    	PURC_GRP_NM = #{p.purc_grp_nm}
	    	,PURC_GRP_NM_EN = #{p.purc_grp_nm_en}
	    	,USE_YN = #{p.use_yn}
	    	,MODR_ID = #{g.username}
	    	,MOD_DTTM = #{g.now}
		WHERE
			TEN_ID = #{g.tenant}
			AND PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}
			AND PURC_GRP_CD = #{p.purc_grp_cd}

mapper.delete:
    /* job.deleteListJob */
    	DELETE 
    	  FROM PURC_GRP
    	 WHERE TEN_ID 		= #{g.tenant}
    	   AND PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}
    	   AND PURC_GRP_CD 	= #{p.purc_grp_cd}
		   AND CO_CD 		= #{p.co_cd}

mapper.select:
/* job.findListPurchaseGroupCategoryJobUser */ SELECT PGP.PURC_GRP_TYP_CCD /*구매 그룹 유형*/ ,PGP.PURC_GRP_CD /*구매 그룹 코드*/ ,PGP.USR_ID /*사용자 아이디*/ ,PGP.TEN_ID /*시스템 아이디*/ ,PGP.CO_CD /*회사 코드*/ ,PGP.RMK /*비고*/ ,PGP.STS /*상태*/ ,PGP.REGR_ID /*등록자 아이디*/ ,PGP.REG_DTTM /*등록 일시*/ ,PGP.MODR_ID /*수정자 아이디*/ ,PGP.MOD_DTTM /*수정 일시*/ , CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END AS DISPLAY_USR_NM /*이름*/ ,USR_NM /*이름*/ ,USR_NM_EN /*이름*/ ,USR.DEPT_CD , LOGIC_ORG.LOGIC_ORG_NM AS COMP_NM , LOGIC_ORG.LOGIC_ORG_NM_EN AS COMP_NM_EN , DP.DEPT_NM , DP.DEPT_NM_EN , CASE WHEN 'en_US' = #{g.locale} THEN LOGIC_ORG.LOGIC_ORG_NM_EN ELSE LOGIC_ORG.LOGIC_ORG_NM END AS DISPLAY_COMP_NM /*회사*/ , CASE WHEN 'en_US' = #{g.locale} THEN (CASE WHEN DP.DEPT_NM_EN IS NULL THEN '' ELSE DP.DEPT_NM_EN END ) ELSE (CASE WHEN DP.DEPT_NM IS NULL THEN '' ELSE DP.DEPT_NM END ) END AS DISPLAY_DEPT_NM /*부서명*/ FROM PURC_GRP_PIC PGP INNER JOIN USR USR ON USR.TEN_ID = PGP.TEN_ID AND USR.CO_CD = PGP.CO_CD AND USR.USR_ID = PGP.USR_ID INNER JOIN LOGIC_ORG LOGIC_ORG ON LOGIC_ORG.TEN_ID = USR.TEN_ID AND LOGIC_ORG.LOGIC_ORG_TYP_CCD = 'COMPANY' AND LOGIC_ORG.LOGIC_ORG_CD = USR.CO_CD LEFT OUTER JOIN LOGIC_ORG_DEPT_MAPG ODM ON USR.TEN_ID = ODM.TEN_ID AND USR.CO_CD = ODM.LOGIC_ORG_CD AND USR.DEPT_CD = ODM.DEPT_CD AND ODM.LOGIC_ORG_TYP_CCD = 'COMPANY' LEFT OUTER JOIN DEPT DP /* C : 부서 */ ON USR.TEN_ID = DP.TEN_ID AND USR.CO_CD = ODM.LOGIC_ORG_CD AND DP.DEPT_CD = ODM.DEPT_CD AND LOGIC_ORG.LOGIC_ORG_TYP_CCD = ODM.LOGIC_ORG_TYP_CCD AND DP.STS != 'D'
AND PGP.TEN_ID = #{g.tenant} AND PGP.STS != 'D'
AND PGP.PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}
AND PGP.PURC_GRP_CD = #{p.purc_grp_cd}
AND PGP.CO_CD = #{p.co_cd}

/* job.findListPurchaseGroupCategoryAndItemMapping */ SELECT TEN_ID /*시스템 아이디*/ ,PURC_GRP_TYP_CCD /*구매 그룹 유형*/ ,PURC_GRP_CD /*구매 그룹 코드*/ ,CO_CD /*회사 코드 */ ,ITEMCAT_CD /*분류 코드 */ ,USE_YN /*사용 여부 */ ,ITEMCAT_YN /*분류 여부 */ ,RMK /*비고 */ ,STS /*상태 */ ,REGR_ID /*등록자 아이디*/ ,REG_DTTM /*등록 일시 */ ,MODR_ID /*수정자 아이디*/ ,MOD_DTTM /*수정 일시 */ FROM PURC_GRP_ITEMCAT
AND TEN_ID = #{g.tenant} AND STS != 'D'
AND PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}
AND PURC_GRP_CD = #{p.purc_grp_cd}
AND CO_CD = #{p.co_cd}

mapper.insert:
/* job.insertPurchaseGroupCategoryJobUser */
INSERT INTO PURC_GRP_PIC
(
	 TEN_ID /*시스템 아이디 */
	,REGR_ID /*등록자 아이디 */
	,REG_DTTM /*등록 일시 */
	,PURC_GRP_TYP_CCD /*구매 그룹 유형 */
	,PURC_GRP_CD /*구매 그룹 코드 */
	,USR_ID /*사용자 아이디 */
	,CO_CD /*회사 코드 */
	,RMK /*비고 */
	,STS /*상태 */
)
VALUES (
	 #{g.tenant}
	,#{g.username}
	,#{g.now}
	,#{p.purc_grp_typ_ccd}
	,#{p.purc_grp_cd}
	,#{p.usr_id}
	,#{p.co_cd}
	,#{p.rmk}
	,'C'
)

mapper.delete:
	/* job.deletePurchaseGroupCategoryJobUserByUserId */
    	DELETE 
    	  FROM PURC_GRP_PIC
    	 WHERE TEN_ID 		= #{g.tenant}
		   AND USR_ID 		= #{p.usr_id} 		/*사용자 아이디 */
		   AND CO_CD 		= #{p.co_cd} 		/*회사 코드 */

mapper.select:
/* findListUsedPurchaseGroupCategory: 직무 목록 조회 */ SELECT TEN_ID /* 시스템 아이디 */ , PURC_GRP_TYP_CCD /* 구매그룹유형 */ , PURC_GRP_CD /* 구매그룹코드 */ , CO_CD /* 회사코드 */ , PURC_GRP_NM , PURC_GRP_NM_EN /* 구매그룹영문명 */ , CASE WHEN 'en_US' = #{g.locale} THEN PURC_GRP_NM_EN ELSE PURC_GRP_NM END DISP_PURC_GRP_NM , USE_YN /* 사용여부 */ , REGR_ID /* 작성자 아이디 */ , REG_DTTM /* 작성일시 */ , MODR_ID /* 수정자 아이디 */ , MOD_DTTM /* 수정일시 */ FROM PURC_GRP /* 구매그룹관리 */ WHERE TEN_ID = #{g.tenant} AND STS != 'D' AND USE_YN = 'Y' AND CO_CD = #{p.co_cd}
AND PURC_GRP_TYP_CCD = #{p.purc_grp_typ_ccd}
AND (UPPER(PURC_GRP_NM_EN) like UPPER(#{namePattern}) OR UPPER(PURC_GRP_NM) like UPPER(#{namePattern}))
AND UPPER(PURC_GRP_CD) like UPPER(#{cdPattern})
ORDER BY PURC_GRP_TYP_CCD, PURC_GRP_CD

mapper.insert:
/* insertPurchaseGroupCategoryAndItemMapping */
	INSERT INTO PURC_GRP_ITEMCAT		/* 품목분류_구매그룹_매핑  */
	(
			  TEN_ID /* 시스템 아이디 */
			, REGR_ID /* 등록자 아이디 */
			, REG_DTTM /* 등록 일시 */
			, MODR_ID /* 수정자 아이디 */
			, MOD_DTTM /* 수정 일시 */
			, STS /* 상태 */

			, PURC_GRP_TYP_CCD /* 구매 그룹 유형 */


			, PURC_GRP_CD /* 구매 그룹 코드 */


			, CO_CD /* 회사 코드 */


			, ITEMCAT_CD /* 분류 코드 */


			, RMK /* 비고 */

	)
	    VALUES (
			  #{g.tenant}
			, #{g.username}
			, #{g.now}
			, #{g.username}
			, #{g.now}
			, 'C'

			, #{p.purc_grp_typ_ccd}


			, #{p.purc_grp_cd}


			, #{p.co_cd}


			, #{p.itemcat_cd}


			, #{p.rmk}

	)
