mapper.select:
/* manual.findListSelectionLanguageManualMenu */ SELECT MM.PAR_MENU_CD, MM.TEN_ID, MM.MENU_CD, MM.MENU_URL, MM.USR_TYP_CCD, MM.MENU_SORT, MM.USE_YN, MM.RMK, MM.STS, MM.MODR_ID, MM.MOD_DTTM, ML.MENU_NM, #{g.locale} AS LANG_CCD, ML.MENU_CD AS ML_MENU_CD, ML2.MENU_NM AS LANG_CCD_MENU_NM, #{p.lang_ccd} AS LANG_CCD2, ML2.MENU_CD AS ML_MENU_CD2 , CASE WHEN MNL.MENU_CD IS NULL THEN 'N' ELSE 'Y' END AS REG_YN , MNL.MNL_AUTO_PSTG_YN , MNL.MNL_AUTO_PSTG_ST_DTTM , MNL.MNL_AUTO_PSTG_EXP_DTTM FROM MENU MM LEFT OUTER JOIN MENU_MULTLANG ML ON ( MM.TEN_ID = ML.TEN_ID AND MM.MENU_CD = ML.MENU_CD AND ML.LANG_CCD = #{g.locale} ) LEFT OUTER JOIN MENU_MULTLANG ML2 ON ( MM.TEN_ID = ML2.TEN_ID AND MM.MENU_CD = ML2.MENU_CD AND ML2.LANG_CCD = #{p.lang_ccd} ) LEFT OUTER JOIN (SELECT * FROM (SELECT ROW_NUMBER() OVER(PARTITION BY MENU_CD,LANG_CCD,MNL_TYP_CCD ORDER BY MNL_REVNO DESC) AS RNUM ,A.* FROM MNL A WHERE TEN_ID = #{g.tenant} AND LANG_CCD = #{p.lang_ccd}) B WHERE B.RNUM = 1) MNL ON ( MNL.TEN_ID = MM.TEN_ID AND MNL.MENU_CD = ML2.MENU_CD AND MNL.LANG_CCD = #{p.lang_ccd} ) WHERE MM.TEN_ID = #{g.tenant}
AND MM.MENU_CD = #{p.menu_cd}
AND MM.STS != 'D'
AND MM.USR_TYP_CCD = #{p.usr_typ_ccd}
ORDER BY MM.MENU_SORT

mapper.update:
	/* manual.updateSelectionRevisionManualInfo */
	       UPDATE MNL
	          SET MODR_ID = #{g.tenant}
	             ,MOD_DTTM = #{g.now}
			     ,MNL_TYP_CCD = #{p.mnl_typ_ccd}	    /*매뉴얼 유형 */
	             ,MNL_PSTG_REVNO = #{p.mnl_pstg_revno}	    /*게시 차수 */
                 ,MNL_ATHG_UUID = #{p.mnl_athg_uuid}	/*매뉴얼 첨부 번호 */
                 ,ORIG_MNL_ATHG_UUID = #{p.orig_mnl_athg_uuid}	/*원본파일 첨부 번호 */
                 ,MNL_CONT = #{p.mnl_cont}	    /*매뉴얼 내용 */
                 ,MNL_AUTO_PSTG_YN = #{p.mnl_auto_pstg_yn}
                 ,MNL_AUTO_PSTG_ST_DTTM = #{p.mnl_auto_pstg_st_dttm}
                 ,MNL_AUTO_PSTG_EXP_DTTM = #{p.mnl_auto_pstg_exp_dttm}
            WHERE TEN_ID  = #{g.tenant}
              AND MENU_CD = #{p.menu_cd}
              AND MNL_REVNO = #{p.mnl_revno}
              AND LANG_CCD = #{p.lang_ccd}

mapper.select:
	/* manual.findLastRevisionManualInfo */
           SELECT TEN_ID	  /*시스템 아이디 */
                 ,MENU_CD	  /*메뉴 코드 */
                 ,MNL_REVNO	  /*매뉴얼 차수 */
                 ,LANG_CCD     /*언어 코드*/
                 ,MNL_TYP_CCD	  /*매뉴얼 유형 */
                 ,MNL_PSTG_REVNO	  /*게시 차수 */
                 ,MNL_ATHG_UUID  /*매뉴얼 첨부 번호 */
                 ,ORIG_MNL_ATHG_UUID   /*원본 매뉴얼 첨부 번호 */
                 ,MNL_CONT	  /*매뉴얼 내용 */
                 ,REGR_ID	  /*등록자 아이디 */
                 ,REG_DTTM	  /*등록 일시 */
                 ,MODR_ID	  /*수정자 아이디 */
                 ,MOD_DTTM	  /*수정 일시 */
                 ,MNL_AUTO_PSTG_YN
                 ,MNL_AUTO_PSTG_ST_DTTM
                 ,MNL_AUTO_PSTG_EXP_DTTM
             FROM MNL
            WHERE TEN_ID  = #{g.tenant}
              AND MENU_CD = #{p.menu_cd}


              AND MNL_REVNO = #{p.mnl_revno}


			  AND MNL_REVNO = (SELECT MAX(MNL_REVNO)
			                   FROM MNL
			                  WHERE TEN_ID = #{g.tenant}
			                    AND MENU_CD = #{p.menu_cd}
			                    AND LANG_CCD = #{p.lang_ccd}
			                )


		      AND LANG_CCD = #{p.lang_ccd}

	/* manual.findListRevisionManualComboboxItem */
           SELECT MNL_REVNO AS DATA
                 ,MNL_REVNO AS LABEL
             FROM MNL
            WHERE TEN_ID  = #{g.tenant}
              AND MENU_CD = #{p.menu_cd}
              AND LANG_CCD = #{p.lang_ccd}
         ORDER BY MNL_REVNO DESC

mapper.delete:
DELETE FROM MNL
WHERE TEN_ID = #{g.tenant}
AND MENU_CD = #{p.menu_cd}

mapper.select:
SELECT COUNT(*)
FROM MNL
WHERE TEN_ID = #{g.tenant}
  AND MENU_CD = #{p.menu_cd}
