mapper.insert:
/* menu-function.insertMenuFunc: 메뉴기능 등록 */
INSERT 
  INTO MENU_ACT (
       TEN_ID
     , MENU_CD
     , ACT_CD
     , ACT_NM
     , REGR_ID
     , REG_DTTM
     , MODR_ID
     , MOD_DTTM
) VALUES (
       #{g.tenant}
     , #{p.menu_cd}
     , #{p.act_cd}
     , #{p.act_nm}
     , #{g.username}
     , #{g.now}
     , #{g.username}
     , #{g.now}
)

mapper.update:
/* menu-function.updateMenuFunc: 메뉴기능 수정 */
UPDATE MENU_ACT
   SET MOD_DTTM = #{g.now}
     , MODR_ID = #{g.username}

     , ACT_NM = #{p.act_nm}

 WHERE TEN_ID = #{g.tenant}
   AND MENU_CD = #{p.menu_cd}
   AND ACT_CD = #{p.act_cd}

mapper.delete:
/* menu-function.deleteMenuFunc: 메뉴기능 삭제 */
DELETE 
  FROM MENU_ACT
 WHERE TEN_ID = #{g.tenant}
   AND MENU_CD = #{p.menu_cd}
   AND ACT_CD = #{p.act_cd}

mapper.select:
/* menu-function.getCountMenuFunc: 메뉴기능 카운트 */
SELECT COUNT(*)
  FROM MENU_ACT
 WHERE TEN_ID = #{g.tenant}
   AND MENU_CD = #{p.menu_cd}
   AND ACT_CD = #{p.act_cd}

mapper.delete:
/* menu-function.deleteMenuFuncAll: 메뉴기능 전체 삭제 */
DELETE 
  FROM MENU_ACT
 WHERE TEN_ID = #{g.tenant}
   AND MENU_CD = #{p.menu_cd}
