mapper.select:
/*findListMenu */ SELECT MM.PAR_MENU_CD, MM.TEN_ID, MM.MENU_CD, MM.MENU_URL, MM.USR_TYP_CCD, MM.MENU_SORT, MM.USE_YN, MM.RMK, MM.STS, MM.MODR_ID, MM.MOD_DTTM, ML.MENU_NM, #{g.locale} AS LANG_CCD, ML.MENU_CD AS ML_MENU_CD, ML2.MENU_NM AS LANG_CCD_MENU_NM, #{p.lang_ccd} AS LANG_CCD2, ML2.MENU_CD AS ML_MENU_CD2 FROM MENU MM LEFT OUTER JOIN MENU_MULTLANG ML ON ( MM.TEN_ID = ML.TEN_ID AND MM.MENU_CD = ML.MENU_CD AND ML.LANG_CCD = #{g.locale} ) LEFT OUTER JOIN MENU_MULTLANG ML2 ON ( MM.TEN_ID = ML2.TEN_ID AND MM.MENU_CD = ML2.MENU_CD AND ML2.LANG_CCD = #{p.lang_ccd} ) WHERE MM.TEN_ID = #{g.tenant}
AND MM.MENU_CD = #{p.menu_cd}
AND MM.STS != 'D'
AND MM.USR_TYP_CCD = #{p.usr_typ_ccd}
ORDER BY MM.MENU_SORT

mapper.delete:
DELETE FROM MENU
WHERE TEN_ID = #{g.tenant}
AND	MENU_CD = #{p.menu_cd}

mapper.update:
UPDATE MENU_MULTLANG
SET
	MENU_NM = #{p.menu_nm},
	STS = 'U',
	MODR_ID = #{g.username},
	MOD_DTTM = #{g.now}
WHERE
	TEN_ID = #{g.tenant}
	AND MENU_CD = #{p.menu_cd}
	AND LANG_CCD = #{g.locale}

UPDATE MENU_MULTLANG
 SET
	MENU_NM = #{p.menu_nm},
	STS = 'U',
	MODR_ID = #{g.username},
	MOD_DTTM = #{g.now}
 WHERE
	TEN_ID = #{g.tenant}
	AND MENU_CD = #{p.menu_cd}
	AND LANG_CCD = #{p.lang_ccd}

mapper.delete:
