mapper.insert:
/* operUnit.insertOperationUnit: 운영단위 등록 */
INSERT 
  INTO OUNIT (
       TEN_ID
     , OUNIT_CD
     , OUNIT_NM
     , OUNIT_NM_EN
     , RMK
     , STS
     , REGR_ID
     , REG_DTTM
     , MODR_ID
     , MOD_DTTM
) VALUES (
       #{g.tenant}
     , #{p.ounit_cd}
     , #{p.ounit_nm}
     , #{p.ounit_nm_en}
     , #{p.rmk}
     , 'C'
     , #{g.username}
     , #{g.now}
     , #{g.username}
     , #{g.now}
)

mapper.update:
/* operUnit.updateOperationUnit: 운영단위 수정 */
UPDATE OUNIT
   SET STS = 'U'
     , MOD_DTTM = #{g.now}
     , MODR_ID = #{g.username}
     , OUNIT_NM = #{p.ounit_nm}
     , OUNIT_NM_EN = #{p.ounit_nm_en}
     , RMK = #{p.rmk}
 WHERE TEN_ID = #{g.tenant}
   AND OUNIT_CD = #{p.ounit_cd}

mapper.delete:
/* operUnit.deleteOperationUnit: 운영단위 삭제 */
DELETE 
  FROM OUNIT
 WHERE TEN_ID = #{g.tenant}
   AND OUNIT_CD = #{p.ounit_cd}

mapper.select:
/* operUnit.getCountOperationUnitByOperationUnitCode: 운영단위 카운트 by 운영단위코드 */
SELECT COUNT(*)
  FROM OUNIT
 WHERE TEN_ID = #{g.tenant}
   AND OUNIT_CD = #{p.ounit_cd}
