mapper.select:
		SELECT SUBSTR(POHD.PO_CRN_DT, 1, 4)    AS YEAR
       , SUM(POHD.PO_AMT)              AS PO_AMT
	    FROM PO POHD
	        WHERE POHD.TEN_ID        =  #{g.tenant}

	        AND POHD.OORG_CD IN

           #{oorg_cd}


	       AND POHD.EFCT_PO_YN    = 'Y'
	       AND POHD.PO_STS_CCD   = 'APVD'

	       AND SUBSTR(POHD.PO_CRN_DT, 1, 4)   = #{getFormatNowYear}
	     GROUP BY SUBSTR(POHD.PO_CRN_DT, 1, 4)

		SELECT SUBSTR(POHD.PO_CRN_DT, 1, 4)    AS YEAR
       , SUBSTR(POHD.PO_CRN_DT, 5, 2)    AS MONTH
       , SUM(POHD.PO_AMT)              AS PO_AMT
	    FROM PO POHD
	        WHERE POHD.TEN_ID        =  #{g.tenant}

	        AND POHD.OORG_CD IN

           #{oorg_cd}


	       AND POHD.EFCT_PO_YN    = 'Y'
	       AND POHD.PO_STS_CCD   = 'APVD'


	       AND POHD.PO_CRN_DT   >= #{getFormatTreeDate}
	       AND POHD.PO_CRN_DT   <= #{getFormatNow}
	     GROUP BY SUBSTR(POHD.PO_CRN_DT, 1, 4), SUBSTR(POHD.PO_CRN_DT, 5, 2)
	     ORDER BY SUBSTR(POHD.PO_CRN_DT, 1, 4) DESC, SUBSTR(POHD.PO_CRN_DT, 5, 2)

/* portal-widget.findListExchange: 환율API 리스트 조회 */ SELECT EXCH.TEN_ID /* 시스템 아이디 */ , EXCH.CUR_UNIT /* 통화코드 */ , CD.CUR_UNIT_NM /* 통화코드 명 */ , EXCH.TTB /* 전신환(송금)받으실때 */ , EXCH.TTS /* 전신환(송금)보내실때 */ , EXCH.DEAL_BAS_R /* 매매 기준율 */ , PREV_EXCH.DEAL_BAS_R /* 전일 매매 기준율 */ , CASE WHEN PREV_EXCH.DEAL_BAS_R IS NOT NULL THEN EXCH.DEAL_BAS_R - PREV_EXCH.DEAL_BAS_R ELSE NULL END AS DIFF_DEAL_BAS_R /* 전일대비 */ , CASE WHEN PREV_EXCH.DEAL_BAS_R IS NOT NULL THEN ROUND((EXCH.DEAL_BAS_R - PREV_EXCH.DEAL_BAS_R) / PREV_EXCH.DEAL_BAS_R * 100, 2) ELSE NULL END AS DIFF_RATE_DEAL_BAS_R /* 등락율 */ , EXCH.BKPR /* 장부가격 */ , EXCH.YY_EFEE_R /* 년환가료율 */ , EXCH.TEN_DD_EFEE_R /* 10일환가료율 */ , EXCH.KFTC_DEAL_BAS_R /* 서울외국환중계 매매기준율 */ , EXCH.KFTC_BKPR /* 서울외국환중계 장부가격 */ , EXCH.STS /* 상태 */ , EXCH.REGR_ID /* 등록자 아이디 */ , EXCH.REG_DTTM /* 등록일시 */ , EXCH.MODR_ID /* 수정자아이디 */ , EXCH.MOD_DTTM /* 수정일시 */ FROM EXCHRATE_IF EXCH LEFT OUTER JOIN EXCHRATE_IF PREV_EXCH ON PREV_EXCH.TEN_ID = EXCH.TEN_ID
AND PREV_EXCH.SEARCHDATE = #{compareDate} AND PREV_EXCH.CUR_UNIT = EXCH.CUR_UNIT INNER JOIN ( SELECT CDDT.TEN_ID , CDDT.DTLCD AS CUR_UNIT , CDDL.DTLCD_NM AS CUR_UNIT_NM FROM CCD CDGP INNER JOIN DTLCD CDDT ON CDDT.TEN_ID = CDGP.TEN_ID AND CDDT.CCD = CDGP.CCD INNER JOIN DTLCD_MULTLANG CDDL ON CDDL.TEN_ID = CDDT.TEN_ID AND CDDL.CCD = CDDT.CCD AND CDDL.DTLCD = CDDT.DTLCD AND CDDL.LANG_CCD = #{g.locale} INNER JOIN CCD_CSTR_CND_VAL CCCV ON CCCV.TEN_ID = CDDT.TEN_ID AND CCCV.CCD = CDDT.CCD AND CCCV.DTLCD = CDDT.DTLCD AND CCCV.CSTR_CND_CD = 'PORTAL' AND CCCV.CSTR_CND_VAL = 'Y' WHERE CDGP.TEN_ID = #{g.tenant} AND CDGP.CCD = 'C004' ) CD ON EXCH.TEN_ID = CD.TEN_ID AND EXCH.CUR_UNIT = CD.CUR_UNIT WHERE EXCH.TEN_ID = #{g.tenant}
AND EXCH.SEARCHDATE = #{getFormatNow}

/* portal-widget.findNoticeBoardList */
SELECT BBD.TEN_ID /*시스템 아이디 */ ,BBD.PST_NO /*게시물_번호 */ ,BBD.BBD_UUID /*게시판_아이디 */ ,BBD.PST_GRP_NO /*게시물_그룹_번호 */ ,BBD.PST_GRP_SEQNO /*게시물_그룹_일련번호 */ ,BBD.PST_LVL /*게시물_레벨 */ ,BBD.PST_CAT_ID /*카테고리_아이디 */ ,CONCAT(( BBD.PST_TIT ) , ( CASE WHEN BS.CMNT_USE_YN = 'Y' THEN CONCAT(CONCAT(' (' , ( CASE WHEN BBD.CMNT_CNT IS NULL THEN '0' ELSE CAST(BBD.CMNT_CNT AS VARCHAR(10)) END )) , ')') ELSE '' END )) AS PST_TIT /*게시물_제목 */ ,BBD.PST_CONT /*게시물_내용 */ ,BBD.AUTO_PSTG_ST_DT /*게시물_시작일 */ ,BBD.AUTO_PSTG_EXP_DT /*게시물_종료일 */ ,BBD.REGR_ID /*등록자_아이디 */ ,BBD.CRTR_NM /*등록자_명 */ ,BBD.CRTR_EML /*등록자_이메일 */ ,BBD.MODR_ID /*수정자_아이디 */ ,BBD.MODR_NM /*수정자_명 */ ,BBD.MODR_EML /*수정자_이메일 */ ,( CASE WHEN BBD.VCNT IS NULL THEN 0 ELSE BBD.VCNT END ) AS VCNT /*조회_수 */ ,BBD.ATHF_CNT /*첨부파일_수 */ ,BBD.CMNT_CNT /*댓글_수 */ ,CASE WHEN BBD.ANN_YN IS NULL THEN 'N' ELSE BBD.ANN_YN END AS ANN_YN /*공지사항_여부 */ ,BBD.MAIN_ANN_YN /*메인_공지사항_여부 */ ,BBD.ATHG_UUID /*첨부파일_번호 */ ,BBD.SECTXT_YN /*비밀글 여부*/ ,CASE WHEN BBD.PST_GRP_SEQNO > 0 THEN ( SELECT ORIGIN_BDMA.REGR_ID FROM BBD ORIGIN_BDMA WHERE ORIGIN_BDMA.TEN_ID = BBD.TEN_ID AND ORIGIN_BDMA.PST_GRP_NO = BBD.PST_GRP_NO AND ORIGIN_BDMA.PST_GRP_SEQNO = 0 ) ELSE BBD.REGR_ID END AS ORG_REGR_ID /*원글 작성자(비밀글인 경우 원글 작성자와 관리자만 볼 수 있다.)*/ ,BBD.DEL_YN /*삭제_여부 */ ,BBD.REG_DTTM /*등록_일시 */ ,BBD.MOD_DTTM /*수정_일시 */ FROM BBD BBD INNER JOIN BBD_SETUP BS ON BBD.TEN_ID = BS.TEN_ID WHERE BBD.BBD_UUID = BS.BBD_UUID AND BBD.TEN_ID = #{g.tenant} AND BBD.BBD_UUID = #{p.bbd_uuid} AND BBD.DEL_YN = 'N'
AND BBD.PST_TIT LIKE #{postTitPattern}
AND BBD.PST_CONT LIKE #{postContPattern}
AND BBD.CRTR_NM LIKE #{regNmPattern}
AND ( BBD.PST_TIT LIKE #{postTitContPattern} OR BBD.PST_CONT LIKE #{postTitContPattern} )
ORDER BY ANN_YN DESC , CAST(BBD.PST_GRP_NO AS INT) DESC , CAST(BBD.PST_GRP_SEQNO AS INT)

/* portal-widget.findTodoGroup : 할일 그룹 정보 조회 */
SELECT TODO_GRP_CD
     , TODO_GRP_NM
     , COMM_MENU_YN
  FROM TODO_TASK_TYP
 WHERE TEN_ID  = #{g.tenant}
   AND USR_TYP_CCD = #{g.usr_typ_ccd}

   AND TODO_GRP_CD  = #{todoGrpCd}

	/* portal-widget.countPr */




    SELECT COUNT(PRHD.PR_UUID)  AS CNT
      FROM PR PRHD
     WHERE PRHD.TEN_ID  = #{g.tenant}
       AND PRHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                  FROM OORG_USR OOPM
                                     , OORG OOMG
                                 WHERE OOPM.TEN_ID       = #{g.tenant}
                                   AND OOPM.USR_ID       = #{g.username}
                                   AND OOMG.TEN_ID       = OOPM.TEN_ID
                                   AND OOMG.OUNIT_CD = 'PO'
                                   AND OOMG.OORG_CD  = OOPM.OORG_CD
                                   AND OOMG.STS         != 'D'
                               )
       AND PRHD.PR_STS_CCD  = #{p.pr_sts_ccd}
       AND PRHD.PR_REQ_DT  >= #{prFromDate}
       AND PRHD.PR_REQ_DT  < #{prToDate}
       AND PRHD.STS         != 'D'

	/* portal-widget.countRfx */




	SELECT COUNT(RQHD.RFX_UUID)  AS CNT
      FROM RFX RQHD
     WHERE RQHD.TEN_ID  = #{g.tenant}
       AND RQHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                  FROM OORG_USR OOPM
                                     , OORG OOMG
                                 WHERE OOPM.TEN_ID       = #{g.tenant}
                                   AND OOPM.USR_ID       = #{g.username}
                                   AND OOMG.TEN_ID       = OOPM.TEN_ID
                                   AND OOMG.OUNIT_CD = 'PO'
                                   AND OOMG.OORG_CD  = OOPM.OORG_CD
                                   AND OOMG.STS         != 'D'
                               )



       AND RQHD.RFX_STS_CCD IN

             #{rfx_sts_ccd}



         AND RQHD.RFX_STS_CCD = #{p.rfx_sts_ccd}



       AND RQHD.RFX_TYP_CCD     != 'RA' /* 역경매 제외 */
       AND RQHD.RFX_CRN_DT    >= #{rfxFromDate}
       AND RQHD.RFX_CRN_DT    < #{rfxToDate}
       AND RQHD.STS         != 'D'

	/* portal-widget.countPo */




    SELECT COUNT(POHD.PO_UUID)     AS CNT
      FROM PO POHD
     WHERE POHD.TEN_ID       = #{g.tenant}
       AND POHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                  FROM OORG_USR OOPM
                                     , OORG OOMG
                                 WHERE OOPM.TEN_ID       = #{g.tenant}
                                   AND OOPM.USR_ID       = #{g.username}
                                   AND OOMG.TEN_ID       = OOPM.TEN_ID
                                   AND OOMG.OUNIT_CD = 'PO'
                                   AND OOMG.OORG_CD  = OOPM.OORG_CD
                                   AND OOMG.STS         != 'D'
                               )



       AND POHD.PO_STS_CCD IN

             #{po_sts_ccd}



         AND POHD.PO_STS_CCD = #{p.po_sts_ccd}



         AND POHD.PO_CRN_DT >= #{poFromDate}
         AND POHD.PO_CRN_DT <= #{poToDate}
         AND POHD.STS         != 'D'

	/* portal-widget.countPriceContract */




    SELECT COUNT(INHD.UPRCCNTR_UUID)  AS CNT
      FROM UPRCCNTR INHD
     WHERE INHD.TEN_ID       = #{g.tenant}
       AND INHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                    FROM OORG_USR OOPM
                                       , OORG OOMG
                                   WHERE OOPM.TEN_ID       = #{g.tenant}
                                     AND OOPM.USR_ID       = #{g.username}
                                     AND OOMG.TEN_ID       = OOPM.TEN_ID
                                     AND OOMG.OUNIT_CD = 'PO'
                                     AND OOMG.OORG_CD  = OOPM.OORG_CD
                                     AND OOMG.STS         != 'D'
                                 )



       AND INHD.UPRCCNTR_STS_CCD IN

             #{uprccntr_sts_ccd}



       AND INHD.UPRCCNTR_STS_CCD = #{p.uprccntr_sts_ccd}



       AND INHD.UPRCCNTR_DT >= #{cntrFromDate}
       AND INHD.UPRCCNTR_DT <= #{cntrToDate}
       AND INHD.STS       != 'D'

	/* portal-widget.countAr */




	SELECT COUNT(ARHD.ASN_UUID)  AS CNT
	  FROM ASN ARHD
	 WHERE ARHD.TEN_ID       = #{g.tenant}
	   AND ARHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                  FROM OORG_USR OOPM
                                     , OORG OOMG
                                 WHERE OOPM.TEN_ID       = #{g.tenant}
                                   AND OOPM.USR_ID       = #{g.username}
                                   AND OOMG.TEN_ID       = OOPM.TEN_ID
                                   AND OOMG.OUNIT_CD = 'PO'
                                   AND OOMG.OORG_CD  = OOPM.OORG_CD
                                   AND OOMG.STS         != 'D'
                               )
       AND ARHD.PURC_TYP_CCD     = 'QTY' /* MT:물품 */
       AND ARHD.GR_PIC_ID    = #{g.username}  /* 검수담당자 */

       AND ARHD.ASN_STS_CCD  = #{p.asn_sts_ccd}

       AND ARHD.ASN_DT     >= #{arFromDate}
       AND ARHD.ASN_DT     <= #{arToDate}
       AND ARHD.STS         != 'D'

	/* portal-widget.countGr */




	SELECT COUNT(GRHD.GR_UUID)  AS CNT
	  FROM GR GRHD
	 WHERE GRHD.TEN_ID       = #{g.tenant}
	   AND GRHD.OORG_CD IN (SELECT OOPM.OORG_CD
                                  FROM OORG_USR OOPM
                                     , OORG OOMG
                                 WHERE OOPM.TEN_ID       = #{g.tenant}
                                   AND OOPM.USR_ID       = #{g.username}
                                   AND OOMG.TEN_ID       = OOPM.TEN_ID
                                   AND OOMG.OUNIT_CD = 'PO'
                                   AND OOMG.OORG_CD  = OOPM.OORG_CD
                                   AND OOMG.STS         != 'D'
                               )
       AND GRHD.PURC_TYP_CCD     = 'QTY' /* MT:물품 */
       AND GRHD.GR_PIC_ID    = #{g.username}  /* 검수담당자 */

       AND GRHD.GR_STS_CCD  = #{p.gr_sts_ccd}

       AND GRHD.GR_DT     >= #{grFromDate}
       AND GRHD.GR_DT     <= #{grToDate}
       AND GRHD.STS         != 'D'

/* portal-widget.countSpRfx */




SELECT COUNT(RQHD.RFX_UUID)
  FROM RFX RQHD
  LEFT JOIN RFX_VD RQVD
    ON RQVD.TEN_ID = RQHD.TEN_ID
   AND RQVD.RFX_UUID = RQHD.RFX_UUID
   AND RQVD.VD_CD  = #{g.vd_cd}
   AND RQVD.MULTRND_SUBJ_XCEPT_YN = 'N'
   AND RQVD.STS   != 'D'
  LEFT OUTER JOIN RFX_BID QTHD
    ON QTHD.TEN_ID   = RQHD.TEN_ID
   AND QTHD.RFX_NO   = RQHD.RFX_NO
   AND QTHD.RFX_RND  = RQHD.RFX_RND
   AND QTHD.VD_CD    = #{g.vd_cd}
   AND QTHD.RFX_BID_EFCT_YN = 'Y'
 WHERE RQHD.TEN_ID   = #{g.tenant}
   AND RQHD.RFX_TYP_CCD != 'RA' /* 역경매 제외 */
   AND RQHD.RFX_CRN_DT    >= #{rfxFromDate}
   AND RQHD.RFX_CRN_DT    < #{rfxToDate}
   AND RQHD.STS     != 'D'
   AND (
           (
               RQHD.COMP_TYP_CCD != 'P' /*공개가 아닌경우(지명,수의)*/
           AND RQVD.VD_CD        = #{g.vd_cd}
           )
        OR (
               RQHD.COMP_TYP_CCD = 'P' /*공개*/
           AND (
                   (
                       RQHD.SLCTN_TYP_CCD = 'TOT' AND RQHD.RFX_RND != 1 /*종합평가 재견적 건인경우*/
                   AND EXISTS ( SELECT 1
                                  FROM RFX_BID QTHD
                                 WHERE QTHD.TEN_ID   = RQHD.TEN_ID
                                   AND QTHD.RFX_NO   = RQHD.RFX_NO
                                   AND QTHD.RFX_RND  = RQHD.RFX_RND-1
                                   AND QTHD.VD_CD    = #{g.vd_cd}
                                   AND QTHD.RFX_BID_STS_CCD = 'S'
                              )  /*이전차수에 제출한 업체여야 함*/
                   )
                OR (
                       RQHD.SLCTN_TYP_CCD != 'TOT' OR RQHD.RFX_RND = 1
                   )
               )
           )
       )
   AND RQHD.RFX_STS_CCD NOT IN ('T','A')



   AND (
           QTHD.RFX_BID_STS_CCD IS NULL /* P : 미접수 */
        OR QTHD.RFX_BID_STS_CCD = 'T'   /* T : 임시저장 */
       )


   AND QTHD.RFX_BID_STS_CCD = #{p.rfx_bid_sts_ccd}

/* portal-widget.countSpPo */




SELECT COUNT(POHD.PO_UUID)
  FROM PO POHD
 WHERE POHD.TEN_ID       = #{g.tenant}
   AND POHD.VD_CD        = #{g.vd_cd}
   AND POHD.PO_STS_CCD IN ('APVD', 'CHG', 'TRMN') /* C:발주승인완료, M:발주변경, D:발주해지 */

   AND POHD.VD_PO_STS_CCD  = #{p.vd_po_sts_ccd}

   AND POHD.PO_CRN_DT >= #{poFromDate}
   AND POHD.PO_CRN_DT <= #{poToDate}
   AND POHD.STS         != 'D'
