mapper.select:
	/* portlet.findInfoPortlet: 포틀릿 정보 조회 */
	       SELECT TEN_ID
                 ,PORTL_USR_TYP_CCD
                 ,PORTLT_ID
                 ,SORT
                 ,PORTLT_SCR_ATTR
                 ,PORTLT_NM
                 ,USE_YN
             FROM PORTLT
            WHERE TEN_ID     = #{g.tenant}
              AND PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
              AND PORTLT_ID     = #{p.portlt_id}

	/* portlet.findListPortletRoles: 포틀릿 역할 조회 */
           SELECT a.TEN_ID
                 ,a.ROLE_CD
                 ,a.ROLE_NM
                 ,a.ROLE_NM_EN
                 ,CASE WHEN b.PORTLT_ID IS NULL THEN 'N' ELSE 'Y' END AS USE_YN
             FROM ROLE a
  LEFT OUTER JOIN PORTLT_ROLE b
               ON a.TEN_ID = b.TEN_ID
              AND a.ROLE_CD = b.ROLE_CD
              AND b.TEN_ID = #{g.tenant}
              AND b.PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
              AND b.PORTLT_ID     = #{p.portlt_id}
              WHERE a.TEN_ID = #{g.tenant}
         ORDER BY a.ROLE_CD

/* portlet.findDefaultLayout: 포틀릿 기본 레이아웃 조회 */
	SELECT TEN_ID
		  ,PORTL_USR_TYP_CCD
		  ,LYT_TYP
		  ,LYT_SRCCD
		  ,LYT_COL_SIZE
	 FROM PORTL_LYT
	WHERE TEN_ID = #{g.tenant}
	  AND PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
	  AND LYT_TYP = #{p.lyt_typ}
	  AND HOME_SCR_TYP_CCD= #{p.home_scr_typ_ccd}

mapper.update:
/* portlet.updateDefaultLayout: 포틀릿 기본 레이아웃 저장(Update) */
	UPDATE PORTL_LYT
	   SET LYT_SRCCD	= #{p.lyt_srccd}
	   	  ,LYT_COL_SIZE = #{p.lyt_col_size}
		  ,MODR_ID	= #{g.username}
		  ,MOD_DTTM = #{g.now}
	WHERE
		TEN_ID=#{g.tenant}
		AND PORTL_USR_TYP_CCD=#{p.portl_usr_typ_ccd}
		AND LYT_TYP=#{p.lyt_typ}
		AND HOME_SCR_TYP_CCD= #{p.home_scr_typ_ccd}

mapper.insert:
/* portlet.insertDefaultLayout: 포틀릿 기본 레이아웃 저장(Insert) */
	INSERT INTO PORTL_LYT
	(
	      TEN_ID
	    , PORTL_USR_TYP_CCD
	    , LYT_TYP
	    , LYT_SRCCD
	    , LYT_COL_SIZE
	    , HOME_SCR_TYP_CCD
	    , REGR_ID
	    , REG_DTTM
	    , MODR_ID
	    , MOD_DTTM
	)
    VALUES
	(
	      #{g.tenant}
	    , #{p.portl_usr_typ_ccd}
	    , #{p.lyt_typ}
    	, #{p.lyt_srccd}
    	, #{p.lyt_col_size}
    	, #{p.home_scr_typ_ccd}
	    , #{g.username}
	    , #{g.now}
	    , #{g.username}
	    , #{g.now}
	)

mapper.update:
	       UPDATE PORTLT
	          SET MODR_ID   = #{g.username}
                 ,MOD_DTTM   = #{g.now}
                 ,PORTLT_NM = #{p.portlt_nm}
                 ,SORT = #{p.sort}
                 ,USE_YN = #{p.use_yn}
                 ,PORTLT_SCR_ATTR = #{p.portlt_scr_attr}
            WHERE TEN_ID     = #{g.tenant}
              AND PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
              AND PORTLT_ID     = #{p.portlt_id}

mapper.delete:
	       DELETE 
	         FROM PORTLT
	        WHERE TEN_ID     = #{g.tenant}
              AND PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
              AND PORTLT_ID     = #{p.portlt_id}

DELETE
	FROM PORTLT_ROLE
WHERE 
	TEN_ID     = #{g.tenant}
	AND PORTL_USR_TYP_CCD = #{p.portl_usr_typ_ccd}
	AND PORTLT_ID     = #{p.portlt_id}

		AND ROLE_CD  = #{p.role_cd}
