mapper.select:
/* printout.findListDocumentOutputManagement */ SELECT TEN_ID /*시스템 아이디*/ , DOC_OUTP_UUID /*문서 출력 UUID*/ , CAT_ID /*카테고리 아이디*/ , CAT_EXPLN /*카테고리 설명*/ , DOC_ID /*문서 아이디*/ , DOC_EXPLN /*문서 설명*/ , TASK_TYP_CCD /*업무 유형 공통코드*/ , SCR_HGT_SIZE /*화면 높이 사이즈*/ , SCR_WID_SIZE /*화면 가로 사이즈*/ , DOC_CALL_CD /*문서 호출 코드*/ , RMK /*비고*/ , STS /*상태*/ , REGR_ID /*등록자 아이디*/ , REG_DTTM /*등록 일시*/ , MODR_ID /*수정자 아이디*/ , MOD_DTTM /*수정 일시*/ FROM DOC_OUTP_MGMT WHERE TEN_ID = #{g.tenant}
AND DOC_OUTP_UUID = #{p.doc_outp_uuid}
AND TASK_TYP_CCD = #{p.task_typ_ccd}
AND UPPER(DOC_ID) LIKE UPPER(#{docIdPattern})
AND UPPER(DOC_EXPLN) LIKE UPPER(#{docExplnPattern})
AND STS != 'D' ORDER BY TASK_TYP_CCD

mapper.insert:
		/* printout.insertListDocumentOutputManagement */
		INSERT INTO DOC_OUTP_MGMT

           	TEN_ID				/*테넌트 아이디*/
		   ,DOC_OUTP_UUID		/*문서 UUID*/
           ,CAT_ID				/*카테고리 아이디*/
           ,CAT_EXPLN			/*카테고리 설명*/
           ,DOC_ID				/*문서 아이디*/
           ,DOC_EXPLN			/*문서 설명*/
           ,TASK_TYP_CCD		/*업무 유형 공통코드*/
           ,SCR_HGT_SIZE		/*화면 높이 사이즈*/
           ,SCR_WID_SIZE		/*화면 가로 사이즈*/
           ,DOC_CALL_CD			/*문서 호출 코드*/
           ,STS					/*상태*/
           ,REGR_ID				/*등록자 아이디*/
           ,REG_DTTM			/*등록 일시*/


	   		#{g.tenant}
	   	   ,#{p.doc_outp_uuid}
	   	   ,#{p.cat_id}
	   	   ,#{p.cat_expln}
   		   ,#{p.doc_id}
   		   ,#{p.doc_expln}
   		   ,#{p.task_typ_ccd}
   		   ,#{p.scr_hgt_size}
   		   ,#{p.scr_wid_size}
   		   ,#{p.doc_call_cd}
   		   ,'C'
   		   ,#{g.username}
   		   ,#{g.now}

mapper.delete:
/* printout.deleteDocumentOutputManagment */
DELETE 
  FROM DOC_OUTP_MGMT
 WHERE TEN_ID = #{g.tenant}
   AND DOC_OUTP_UUID = #{p.doc_outp_uuid}

mapper.select:
/* printout.findListDocumentOutputSetup */ SELECT TEN_ID /*시스템 아이디*/ , DOC_OUTP_DAT_SETUP_UUID /*문서 출력 데이터 설정 UUID*/ , DOC_OUTP_UUID /*문서 출력 UUID*/ , DOC_OUTP_DAT_NM /*문서 출력 데이터 명*/ , DOC_OUTP_DAT_EXPLN /*문서 출력 데이터 설명*/ , RMK /*비고*/ , STS /*상태*/ , REGR_ID /*등록자 아이디*/ , REG_DTTM /*등록 일시*/ , MODR_ID /*수정자 아이디*/ , MOD_DTTM /*수정 일시*/ FROM DOC_OUTP_DAT_SETUP WHERE TEN_ID = #{g.tenant}
AND DOC_OUTP_UUID = #{p.doc_outp_uuid}
AND STS != 'D' ORDER BY DOC_OUTP_DAT_NM

mapper.update:
	/* printout.updateListDocumentOutputSetup */
	UPDATE DOC_OUTP_DAT_SETUP
	   SET DOC_OUTP_DAT_EXPLN = #{p.doc_outp_dat_expln} /*문서 데이터 설명*/
		 , STS    		 = 'U'
         , MODR_ID 		 = #{g.username}
         , MOD_DTTM 	 = #{g.now}
     WHERE TEN_ID = #{g.tenant}
       AND DOC_OUTP_DAT_SETUP_UUID = #{p.doc_outp_dat_setup_uuid}

mapper.insert:
		/* printout.insertListDocumentOutputSetup */
		INSERT INTO DOC_OUTP_DAT_SETUP

       	    TEN_ID						/*시스템 아이디*/
	   	   ,DOC_OUTP_DAT_SETUP_UUID    	/*문서 데이터 설정 아이디(UUID)*/
           ,DOC_OUTP_UUID				/*문서 마스터 아이디(UUID)*/
           ,DOC_OUTP_DAT_NM  			/*문서 데이터 명*/
           ,DOC_OUTP_DAT_EXPLN			/*문서 데이터 설명*/
           ,STS							/*상태*/
           ,REGR_ID						/*등록자 아이디*/
           ,REG_DTTM					/*등록 일시*/


   			#{g.tenant}
   		   ,#{p.doc_outp_dat_setup_uuid}
   		   ,#{p.doc_outp_uuid}
   		   ,#{p.doc_outp_dat_nm}
   		   ,#{p.doc_outp_dat_expln}
   		   ,'C'
   		   ,#{g.username}
   		   ,#{g.now}

mapper.delete:
/* printout.deleteDocumentOutputDataSetup */
DELETE 
  FROM DOC_OUTP_DAT_SETUP
 WHERE TEN_ID = #{g.tenant}
   AND DOC_OUTP_DAT_SETUP_UUID = #{p.doc_outp_dat_setup_uuid}

/* printout.deleteDocumentOutputDataSetupByManagement */
DELETE 
  FROM DOC_OUTP_DAT_SETUP
 WHERE TEN_ID = #{g.tenant}
   AND DOC_OUTP_UUID = #{p.doc_outp_uuid}

mapper.select:
/* printout.findListDocumentOutputDataParam */ SELECT TEN_ID /*테넌트 아이디*/ ,DOC_OUTP_DAT_PARM_UUID /*문서 출력 데이터 파라미터 UUID*/ ,DOC_OUTP_DAT_SETUP_UUID /*문서 출력 데이터 설정 UUID*/ ,DOC_OUTP_UUID /*문서 출력 UUID*/ ,DOC_OUTP_DAT_PARM_NM /*문서 출력 데이터 파라미터 명*/ ,DOC_OUTP_DAT_PARM_EXPLN /*문서 출력 데이터 파라미터 설명*/ ,RMK /*비고*/ ,STS /*상태*/ ,REGR_ID /*등록자 아이디*/ ,REG_DTTM /*등록 일시*/ ,MODR_ID /*수정자 아이디*/ ,MOD_DTTM /*수정 일시*/ FROM DOC_OUTP_DAT_PARM WHERE TEN_ID = #{g.tenant} AND DOC_OUTP_UUID = #{p.doc_outp_uuid} AND DOC_OUTP_DAT_SETUP_UUID = #{p.doc_outp_dat_setup_uuid}
AND DOC_OUTP_DAT_PARM_UUID = #{p.doc_outp_dat_parm_uuid}
AND STS != 'D' ORDER BY DOC_OUTP_DAT_PARM_NM

mapper.update:
	/* printout.updateListDocumentOutputDataParam */
	UPDATE DOC_OUTP_DAT_PARM
	   SET DOC_OUTP_DAT_PARM_NM 		= #{p.doc_outp_dat_parm_nm}		/*문서 데이터 설명*/
	   	 , DOC_OUTP_DAT_PARM_EXPLN 		= #{p.doc_outp_dat_parm_expln}		/*문서 데이터 설명*/
	   	 , STS    		 				= 'U'
       	 , MODR_ID 		 				= #{g.username}
       	 , MOD_DTTM 		 			= #{g.now}
   	 WHERE TEN_ID = #{g.tenant}
       AND DOC_OUTP_DAT_SETUP_UUID = #{p.doc_outp_dat_setup_uuid}
       AND DOC_OUTP_DAT_PARM_UUID = #{p.doc_outp_dat_parm_uuid}

mapper.insert:
