mapper.insert:
/* role-function.insertRoleFunc: 롤기능 등록 */
INSERT 
  INTO ROLE_ACT (
       TEN_ID
     , ROLE_CD
     , MENU_CD
     , ACT_CD
     , REGR_ID
     , REG_DTTM
) VALUES (
       #{g.tenant}
     , #{p.role_cd}
     , #{p.menu_cd}
     , #{p.act_cd}
     , #{g.username}
     , #{g.now}
)

mapper.delete:
/* role-function.deleteRoleFunc: 롤기능 삭제 */
DELETE 
  FROM ROLE_ACT
 WHERE TEN_ID = #{g.tenant}
   AND ROLE_CD = #{p.role_cd}
   AND MENU_CD = #{p.menu_cd}
   AND ACT_CD = #{p.act_cd}

mapper.select:
/* role-function.getCountRoleFunc: 롤기능 카운트 */
SELECT COUNT(*)
  FROM ROLE_ACT
 WHERE TEN_ID = #{g.tenant}

   AND ROLE_CD = #{p.role_cd}

   AND MENU_CD = #{p.menu_cd}
   AND ACT_CD = #{p.act_cd}

mapper.delete:
/* role-function.deleteRoleFuncAll : 매핑된 롤메뉴 삭제 */
    DELETE FROM ROLE_ACT
     WHERE TEN_ID  = #{g.tenant}
       AND MENU_CD = #{p.menu_cd}

DELETE
FROM ROLE_ACT
WHERE
TEN_ID = #{g.tenant}
AND
ROLE_CD = #{p.role_cd}

    AND MENU_CD = #{p.menu_cd}
