mapper.insert:
/* terms.insertTerms : 이용약관 마스터 저장 */
INSERT INTO TERMCND(
	  TEN_ID
	, CTMPL_UUID
	, TERMCND_UUID
	, TERMCND_TYP_CCD
	, TERMCND_REN_REVNO
	, EFCT_ST_DT
	, EFCT_EXP_DT
	, NEW_JOIN_USE_YN
	, MAND_CNST_YN
	, REGR_ID
	, REG_DTTM
	, MODR_ID
	, MOD_DTTM
) VALUES(
  	  #{g.tenant}
	, #{p.ctmpl_uuid}
	, #{p.termcnd_uuid}
	, #{p.termcnd_typ_ccd}
	, (SELECT CASE WHEN MAX(TERMCND_REN_REVNO) IS NULL
	               THEN 1
	               ELSE MAX(TERMCND_REN_REVNO) + 1
	           END 
	   FROM TERMCND
	   WHERE TEN_ID    = #{g.tenant}
	   AND   TERMCND_TYP_CCD = #{p.termcnd_typ_ccd})
	, #{p.efct_st_dt}
	, #{p.efct_exp_dt}
	, #{p.new_join_use_yn}
	, #{p.mand_cnst_yn}
	, #{g.username}
	, #{g.now}
	, #{g.username}
	, #{g.now}
)

mapper.select:
/* terms.findPreviousTermsMasterInfo : 이전 차수 약관 상세 조회 */
	SELECT PREV.TEN_ID                 /* 시스템 아이디 */
	     , PREV.CTMPL_UUID                 /* 템플릿 아이디 */
	     , PREV.TERMCND_UUID                  /* 약관 아이디 */
	     , PREV.TERMCND_TYP_CCD              /* 템플릿 구분 */
	     , PREV.TERMCND_REN_REVNO                 /* 약관 차수 */
	     , PREV.EFCT_ST_DT       /* 적용 시작일 */
	     , PREV.EFCT_EXP_DT         /* 적용 종료일 */
	FROM   TERMCND PREV
	WHERE  PREV.TEN_ID = #{g.tenant}
	AND    EXISTS (
					 SELECT 'X'
				     FROM   TERMCND
				     WHERE  TEN_ID    = #{g.tenant}
				     AND    CTMPL_UUID    = #{p.ctmpl_uuid}
				     AND    TERMCND_UUID     = #{p.termcnd_uuid}
				     AND    TEN_ID    = PREV.TEN_ID
				     AND    TERMCND_TYP_CCD = PREV.TERMCND_TYP_CCD
				     AND    TERMCND_REN_REVNO-1  = PREV.TERMCND_REN_REVNO
				   )

/* terms.findPostTermsMasterInfo : 다음 차수 약관 상세 조회 */
	SELECT PREV.TEN_ID                 /* 시스템 아이디 */
	     , PREV.CTMPL_UUID                 /* 템플릿 아이디 */
	     , PREV.TERMCND_UUID                  /* 약관 아이디 */
	     , PREV.TERMCND_TYP_CCD              /* 템플릿 구분 */
	     , PREV.TERMCND_REN_REVNO                 /* 약관 차수 */
	     , PREV.EFCT_ST_DT       /* 적용 시작일 */
	     , PREV.EFCT_EXP_DT         /* 적용 종료일 */
	FROM   TERMCND PREV
	WHERE  PREV.TEN_ID = #{g.tenant}
	AND    EXISTS (
					 SELECT 'X'
				     FROM   TERMCND
				     WHERE  TEN_ID    = #{g.tenant}
				     AND    CTMPL_UUID    = #{p.ctmpl_uuid}
				     AND    TERMCND_UUID     = #{p.termcnd_uuid}
				     AND    TEN_ID    = PREV.TEN_ID
				     AND    TERMCND_TYP_CCD = PREV.TERMCND_TYP_CCD
				     AND    TERMCND_REN_REVNO+1  = PREV.TERMCND_REN_REVNO
				   )

mapper.update:
/* terms.updateTermsMasterInfo : 이용 약관 마스터 수정*/
	UPDATE TERMCND
	SET MODR_ID           = #{g.username}
	  , MOD_DTTM           = #{g.now}

	  , EFCT_ST_DT = #{p.efct_st_dt}     /* 적용 시작일 */


	  , EFCT_EXP_DT   = #{p.efct_exp_dt}       /* 적용 종료일 */


	  , NEW_JOIN_USE_YN = #{p.new_join_use_yn}     /* 신규 등록 적용 여부 */


	  , MAND_CNST_YN      = #{p.mand_cnst_yn}          /* 필수 동의 여부 */

	WHERE TEN_ID         = #{g.tenant}
	AND   CTMPL_UUID         = #{p.ctmpl_uuid}
	AND   TERMCND_UUID          = #{p.termcnd_uuid}

mapper.select:
	/* terms.existsTermsMasterByTermsConditionId : 이용 약관 마스터 수정*/
         SELECT CASE WHEN EXISTS (
         							SELECT 1 
                                  	FROM TERMCND
                                  	WHERE TEN_ID = #{g.tenant}
						            AND   CTMPL_UUID = #{p.ctmpl_uuid}
						            AND   TERMCND_UUID  = #{p.termcnd_uuid}
						          ) 
				     THEN 'Y' 
				     ELSE 'N' 
				END AS EXISTS_YN 
		 FROM DUM_TBL

mapper.delete:
/* terms.deleteContent: 템플릿 본문 삭제 */
	DELETE 
	FROM CTMPL
	WHERE TEN_ID = #{g.tenant}
	AND   CTMPL_UUID = #{p.ctmpl_uuid}

/* terms.deleteTerms: 이용 약관 마스터 삭제 */
	DELETE 
	FROM TERMCND
	WHERE TEN_ID = #{g.tenant}
	AND   CTMPL_UUID = #{p.ctmpl_uuid}
	AND   TERMCND_UUID  = #{p.termcnd_uuid}

/* terms.deleteTerms: 담당자 약관 동의 삭제 */
	DELETE 
	FROM TERMCND_CNST_HISTREC
	WHERE TEN_ID = #{g.tenant}
	AND   CTMPL_UUID = #{p.ctmpl_uuid}
	AND   TERMCND_UUID  = #{p.termcnd_uuid}

mapper.select:
/* terms.existChargeTermsAgreeInfoByTermsConditionsAgreeId: 협력사 약관 동의 여부 체크 */ SELECT CASE WHEN TC.CUR_TC_CNT = TC.VD_TC_CNT THEN 'N' ELSE 'Y' END AS TERMS_POPUP_YN FROM ( SELECT ( SELECT COUNT(*) AS CNT FROM TERMCND WHERE TEN_ID = #{g.tenant}
AND #{getFormatNow} BETWEEN EFCT_ST_DT AND EFCT_EXP_DT AND NEW_JOIN_USE_YN = 'Y' ) AS CUR_TC_CNT , ( SELECT COUNT(*) FROM TERMCND TCMA INNER JOIN TERMCND_CNST_HISTREC CHTC ON TCMA.TEN_ID = CHTC.TEN_ID AND TCMA.CTMPL_UUID = CHTC.CTMPL_UUID AND TCMA.TERMCND_UUID = CHTC.TERMCND_UUID WHERE TCMA.TEN_ID = #{g.tenant}
AND #{getFormatNow} BETWEEN TCMA.EFCT_ST_DT AND TCMA.EFCT_EXP_DT AND CHTC.TERMCND_CNST_USR_UUID = #{g.username} AND TCMA.NEW_JOIN_USE_YN = 'Y' ) AS VD_TC_CNT FROM DUM_TBL ) TC

/* terms.findListNewRegistrationApplyChargeTermsTemplateInfo : 협력사 유효한 약관 정보 조회 */ SELECT TMPM.TEN_ID /* 시스템 아이디 */ , TMPM.CTMPL_UUID /* 템플릿 아이디 */ , MULT.CTMPL_CONT /* 템플릿 내용 */ , TMPM.TMPL_TYP_CCD /* 템플릿 구분 */ , MULT.CTMPL_NM /* 템플릿 명 */ , TMPM.BASC_CTMPL_CD /* 템플릿 기초 아이디 */ , TCMA.TERMCND_UUID /* 약관 아이디 */ , TCMA.TERMCND_TYP_CCD /* 약관 구분 코드 */ , TCMA.TERMCND_REN_REVNO /* 약관 차수 */ , TCMA.EFCT_ST_DT /* 적용 시작일 */ , TCMA.EFCT_EXP_DT /* 적용 종료일 */ , TCMA.NEW_JOIN_USE_YN /* 신규 등록 적용 여부 */ , TCMA.MAND_CNST_YN /* 필수 동의 여부 */ FROM CTMPL TMPM LEFT OUTER JOIN CTMPL_MULTLANG MULT ON TMPM.TEN_ID = MULT.TEN_ID AND TMPM.CTMPL_UUID = MULT.CTMPL_UUID AND MULT.LANG_CCD = #{p.lang_ccd} INNER JOIN TERMCND TCMA ON TMPM.TEN_ID = TCMA.TEN_ID AND TMPM.CTMPL_UUID = TCMA.CTMPL_UUID WHERE TCMA.TEN_ID = #{g.tenant}
AND #{getFormatNow} BETWEEN TCMA.EFCT_ST_DT AND TCMA.EFCT_EXP_DT AND TCMA.NEW_JOIN_USE_YN = 'Y' AND NOT EXISTS ( SELECT 'X' FROM TERMCND MA INNER JOIN TERMCND_CNST_HISTREC TC ON MA.TEN_ID = TC.TEN_ID AND MA.CTMPL_UUID = TC.CTMPL_UUID AND MA.TERMCND_UUID = TC.TERMCND_UUID WHERE MA.TEN_ID = #{g.tenant}
AND #{getFormatNow} BETWEEN MA.EFCT_ST_DT AND MA.EFCT_EXP_DT AND TC.TERMCND_CNST_USR_UUID = #{g.username} AND MA.NEW_JOIN_USE_YN = 'Y' AND MA.TEN_ID = TCMA.TEN_ID AND MA.CTMPL_UUID = TCMA.CTMPL_UUID AND MA.TERMCND_UUID = TCMA.TERMCND_UUID )

mapper.delete:
/* template.deleteChargeTermsAgreeInfoByTemplateBaseId */
DELETE FROM TERMCND_CNST_HISTREC
WHERE CTMPL_UUID IN (SELECT CTMPL_UUID FROM CTMPL
				 WHERE TEN_ID = #{g.tenant}
				   AND BASC_CTMPL_CD = #{p.basc_ctmpl_cd})

/* template.deleteTermsMasterByTemplateBaseId */
DELETE FROM TERMCND
WHERE CTMPL_UUID IN (SELECT CTMPL_UUID FROM CTMPL
				 WHERE TEN_ID = #{g.tenant}
				   AND BASC_CTMPL_CD = #{p.basc_ctmpl_cd})

mapper.insert:
/* terms.insertTermsTemplateContent : 템플릿 본문 신규저장 */
INSERT INTO CTMPL(
TEN_ID
, CTMPL_UUID

	, TMPL_TYP_CCD
	, BASC_CTMPL_CD

, STS
, REGR_ID
, REG_DTTM
, MODR_ID
, MOD_DTTM
)
VALUES(
#{g.tenant}
,#{p.ctmpl_uuid}

	,(SELECT TMPL_TYP_CCD FROM ELEM_COMM_TMPL WHERE ELEM_COMM_TMPL.BASC_CTMPL_CD = #{p.basc_ctmpl_cd} AND ELEM_COMM_TMPL.TEN_ID = #{g.tenant})
	,#{p.basc_ctmpl_cd}

,'C'
,#{g.username}
,#{g.now}
,#{g.username}
,#{g.now}
)

/*   terms.insertTermsTemplateContentMultiLang  템플릿 본문 다국어 신규저장   */
INSERT INTO CTMPL_MULTLANG(
TEN_ID
, CTMPL_UUID
, CTMPL_NM
, CTMPL_CONT

	, CTMPL_PARM_INFO

, LANG_CCD
, STS
, REGR_ID
, REG_DTTM
, MODR_ID
, MOD_DTTM
)
VALUES(
#{g.tenant}
,#{p.ctmpl_uuid}
,#{p.ctmpl_nm}
,#{p.ctmpl_cont}

	,#{p.ctmpl_parm_info}

, #{p.lang_ccd}
,'C'
,#{g.username}
,#{g.now}
,#{g.username}
,#{g.now}
)

mapper.update:
/* template.updateTermsTemplateContent : 템플릿 본문 수정*/
UPDATE CTMPL
SET STS='U'
, MODR_ID = #{g.username}
, MOD_DTTM = #{g.now}

	, BASC_CTMPL_CD = #{p.basc_ctmpl_cd}

WHERE TEN_ID = #{g.tenant}
AND CTMPL_UUID = #{p.ctmpl_uuid}

/* template.updateTermsTemplateContentMultiLang : 템플릿 본문 수정*/
UPDATE CTMPL_MULTLANG
SET STS='U'
, MODR_ID = #{g.username}
, MOD_DTTM = #{g.now}

	, CTMPL_NM = #{p.ctmpl_nm}


	, CTMPL_PARM_INFO = #{p.ctmpl_parm_info}


	, CTMPL_CONT = #{p.ctmpl_cont}

WHERE TEN_ID = #{g.tenant}
AND CTMPL_UUID = #{p.ctmpl_uuid}
AND LANG_CCD = #{p.lang_ccd}

mapper.select:
SELECT COUNT(*)
FROM CTMPL_MULTLANG
WHERE TEN_ID = #{g.tenant}
  AND  CTMPL_UUID = #{p.ctmpl_uuid}
  AND LANG_CCD = #{p.lang_ccd}
