mapper.insert:
    INSERT INTO SYS_ATTR(
		TEN_ID,
		SYS_ATTR_ID,
		SYS_ATTR_DAT
    )VALUES(
        #{g.tenant},
    	'cacheBust',
    	#{p}
    )

mapper.update:
UPDATE SYS_ATTR
	SET SYS_ATTR_DAT = #{p}
WHERE SYS_ATTR_ID = 'cacheBust'

mapper.select:
SELECT USR_PZN_DAT_UUID
	 , USR_PZN_DAT
	 , USR_ID
FROM   USR_PZN_DAT
WHERE  TEN_ID = #{g.tenant}
AND    USR_ID = #{g.username}

	AND    USR_PZN_DAT_UUID = #{p}

mapper.insert:
INSERT INTO USR_PZN_DAT
TEN_ID, USR_PZN_DAT_UUID, USR_PZN_DAT, USR_ID
#{g.tenant}, #{p.id}, #{p.value}, #{g.username}

mapper.update:
UPDATE USR_PZN_DAT SET
	USR_PZN_DAT = #{p.value}
WHERE TEN_ID = #{g.tenant}
AND    USR_ID = #{g.username}
AND   USR_PZN_DAT_UUID = #{p.id}

mapper.delete:
DELETE FROM USR_PZN_DAT
WHERE  TEN_ID = #{g.tenant}
AND    USR_ID = #{g.username}
AND    USR_PZN_DAT_UUID = #{p.id}

DELETE FROM USR_PZN_DAT
WHERE  TEN_ID = #{g.tenant}
AND    USR_ID = #{g.username}
