mapper.select:
		SELECT USR_ID AS USERNAME, PWD AS PASSWORD
		  FROM USR
		WHERE USR_ID = #{p}
		AND TEN_ID = #{g.tenant}
        AND USR_TYP_CCD ='BUYER'
		AND  STS != 'D'

		SELECT USR_ID AS USERNAME, PWD AS PASSWORD
		  FROM USR
		WHERE USR_ID = #{p}
		AND TEN_ID = #{g.tenant}
          AND USR_TYP_CCD ='VD'
		AND  STS != 'D'

mapper.update:
UPDATE USR
	SET PWD_INPERR_CNT = #{p.pwd_inperr_cnt}
	, ACCT_LCKD_YN = #{p.acct_lckd_yn}
WHERE USR_ID = #{p.usr_id}
AND TEN_ID = #{g.tenant}

UPDATE USR
	SET LAST_LOGIN_IP = #{p.last_login_ip}
	, LAST_LOGIN_DTTM = #{g.now}
	, PWD_INPERR_CNT = 0
WHERE USR_ID = #{g.username}
AND TEN_ID = #{g.tenant}

mapper.select:
/* user-session.findListRoleCodeByLoginUserSession */ SELECT AA.ROLE_CD AS "ROLE" FROM ( /* user-role union all dept-role */ SELECT AURP.ROLE_CD /* 사용자-역할 */ FROM ROLE_USR AURP INNER JOIN ROLE AURH ON ( AURP.TEN_ID = AURH.TEN_ID AND AURP.ROLE_CD = AURH.ROLE_CD ) WHERE AURP.TEN_ID = #{g.tenant} AND AURP.USR_ID = #{p.usr_id} AND AURH.USE_YN = 'Y' AND AURP.STS != 'D' AND AURH.STS != 'D'
UNION ALL
SELECT DPROLE.ROLE_CD /* 부서-역할 */ FROM ROLE_DEPT DPROLE INNER JOIN ROLE AURH ON ( DPROLE.TEN_ID = AURH.TEN_ID AND DPROLE.ROLE_CD = AURH.ROLE_CD )
WHERE DPROLE.TEN_ID = #{g.tenant} AND DPROLE.DEPT_CD = #{p.dept_cd} AND AURH.USE_YN = 'Y' AND DPROLE.STS != 'D' AND AURH.STS != 'D' AND EXISTS ( SELECT 1 FROM DEPT DP INNER JOIN LOGIC_ORG_DEPT_MAPG ODM ON DP.TEN_ID = ODM.TEN_ID AND DP.DEPT_CD = ODM.DEPT_CD WHERE DP.TEN_ID = DPROLE.TEN_ID AND ODM.LOGIC_ORG_CD = DPROLE.LOGIC_ORG_CD AND DP.DEPT_CD = DPROLE.DEPT_CD AND DP.USE_YN = 'Y' AND DP.STS != 'D' )
) AA GROUP BY AA.ROLE_CD
