mapper.insert:
/* attach.insertAttach: 첨부파일 등록 */
INSERT 
  INTO ATHF (
       TEN_ID
     , ATHF_UUID
     , ATHG_UUID
     , ATHF_ORIG_NM
     , ATHF_NM
     , ATHF_PATH
     , ATHF_SIZE
     , ATHF_ORIG_DAT
     , RMK
     , SORT
     , STS
     , REGR_ID
     , REG_DTTM
     , MODR_ID
     , MOD_DTTM
) VALUES (
       #{g.tenant}
     , #{p.athf_uuid}
     , #{p.athg_uuid}
     , #{p.athf_orig_nm}
     , #{p.athf_nm}
     , #{p.athf_path}
     , CAST(#{p.athf_size} AS INTEGER)
     , #{p.athf_orig_dat}
     , #{p.rem}
     , CAST(#{p.sort} AS INTEGER)
     , 'C'
     , #{g.username}
     , #{g.now}
     , #{g.username}
     , #{g.now}
)

/* attach.copyAttach: 첨부파일 복사 */
INSERT INTO ATHF
     ( TEN_ID
     , ATHF_UUID
     , ATHG_UUID
     , ATHF_ORIG_NM
     , ATHF_NM
     , ATHF_PATH
     , ATHF_SIZE
     , ATHF_ORIG_DAT
     , RMK
     , SORT
     , STS
     , REGR_ID
     , REG_DTTM
     , MODR_ID
     , MOD_DTTM
     )
SELECT TEN_ID
     , #{p.new_att_id}
     , #{p.new_grp_cd}
     , ATHF_ORIG_NM
     , ATHF_NM
     , ATHF_PATH
     , ATHF_SIZE
     , ATHF_ORIG_DAT
     , RMK
     , #{p.sort,jdbcType=INTEGER}
     , 'C'
     , #{g.username}
     , #{g.now}
     , #{g.username}
     , #{g.now}
  FROM ATHF
 WHERE TEN_ID = #{g.tenant}
   AND ATHF_UUID = #{p.athf_uuid}
   AND ATHG_UUID = #{p.athg_uuid}

mapper.update:
/* attach.deleteAttach: 첨부파일 삭제 */
UPDATE ATHF
   SET STS = 'D'
     , MOD_DTTM = #{g.now}
     , MODR_ID = #{g.username}
 WHERE TEN_ID = #{g.tenant}
   AND ATHF_UUID = #{p.athf_uuid}

mapper.select:
/* attach.findListAttach: 첨부파일 목록 조회 */
SELECT ATHF_UUID
	 , ATHF_ORIG_NM
     , ATHF_PATH
     , ATHF_SIZE
  FROM ATHF
 WHERE TEN_ID = #{g.tenant}
   AND ATHG_UUID = #{p.athg_uuid}
   AND STS != 'D'
 ORDER BY SORT ASC

mapper.update:
/* attach.updateCertFileInfo */
	UPDATE ATHF
	   SET ATHF_ORIG_DAT = #{p.athf_orig_dat}
	 WHERE TEN_ID = #{g.tenant}
	   AND ATHF_UUID = #{p.athf_uuid}
	   AND STS != 'D'
