mapper.select:
/* mobile-attach.findListAttach: 첨부파일 목록 조회 */
SELECT ATHF_UUID
	 , ATHF_ORIG_NM
     , ATHF_PATH
     , ATHF_SIZE
  FROM ATHF
 WHERE TEN_ID = #{p.tenant}
   AND ATHG_UUID = #{p.athg_uuid}
   AND STS != 'D'
 ORDER BY SORT ASC

mapper.update:
/* mobile-attach.deleteAttach: 첨부파일 삭제 */
UPDATE ATHF
   SET STS = 'D'
     , MOD_DTTM = #{g.now}
     , MODR_ID = #{p.username}
 WHERE TEN_ID = #{p.tenant}
   AND ATHF_UUID = #{p.athf_uuid}
