mapper.select:
/*shared.findListVendorMaster : 협력사 마스터 목록 조회 */ SELECT VDGL.VD_CD , CASE WHEN 'ko_KR' = #{g.locale} THEN VDGL.VD_NM ELSE VDGL.VD_NM_EN END AS VD_NM , VDGL.ERP_VD_CD , VDGL.CTRY_CCD , VDGL.BIZREGNO , VDGL.TAX_ID , VDGL.EU_VAT_REG_ID , VDGL.CORPNO FROM VD VDGL WHERE VDGL.TEN_ID = #{g.tenant}
AND VDGL.CTRY_CCD = #{p.ctry_ccd}
AND VDGL.VD_CD LIKE UPPER(#{vdCdPattern})
AND VDGL.ERP_VD_CD LIKE UPPER(#{erpVdCdPattern})
AND VDGL.ERP_VD_CD IS NOT NULL
AND (UPPER(VDGL.VD_NM) LIKE UPPER(#{vdNmPattern}) OR UPPER(VDGL.VD_NM_EN) LIKE UPPER(#{vdNmPattern})) /* 협력사명 검색조건*/
AND VDGL.BIZREGNO LIKE UPPER(#{bizRegNoPattern})
AND VDGL.TAX_ID LIKE UPPER(#{taxNoPattern})
AND VDGL.EU_VAT_REG_ID LIKE UPPER(#{euVatRegNoPattern})
ORDER BY VDGL.ERP_VD_CD

/*shared.findListVendorBasicInfo */ SELECT VDGL.TEN_ID, VDGL.VD_CD, CASE WHEN 'ko_KR' = #{g.locale} THEN VDGL.VD_NM ELSE VDGL.VD_NM_EN END AS VD_NM, VDGL.ERP_VD_CD, VDGL.ERP_VD_CD, VDGL.CORPNO, VDGL.HQ_BIZREGNO, VDGL.CTRY_CCD, WHEN VDGL.CTRY_CCD = 'KR' THEN VDGL.BIZREGNO WHEN VDGL.CTRY_CCD = 'CN' THEN VDGL.TAX_ID WHEN (SELECT COUNT(*) FROM DTLCD WHERE TEN_ID = VDGL.TEN_ID AND CCD = 'C008' AND DTLCD = VDGL.CTRY_CCD) > 0 THEN VDGL.EU_VAT_REG_ID ELSE VDGL.TAX_ID END BIZREGNO, VDGL.VD_EML, CASE WHEN 'ko_KR' = #{g.locale} THEN VDGL.CEO_NM ELSE VDGL.CEO_NM_EN END CEO_NM, CASE WHEN 'ko_KR' = #{g.locale} THEN VDGL.DTL_ADDR_1 ELSE VDGL.DTL_ADDR_EN_1 END DTL_ADDR_1 FROM VD VDGL WHERE VDGL.TEN_ID = #{g.tenant}
AND VDGL.ERP_VD_CD LIKE UPPER(#{cdPattern})
AND (UPPER(VDGL.VD_NM) LIKE UPPER(#{namePattern}) OR UPPER(VDGL.VD_NM_EN) LIKE UPPER(#{namePattern})) /* 협력사명 검색조건*/
ORDER BY VDGL.VD_CD

/*shared.findListVendorInfo : EO 협력사 목록 조회 */ SELECT VD.VD_CD , CASE WHEN 'en_US' = #{g.locale} THEN VD.VD_NM_EN ELSE VD.VD_NM END AS DISP_VD_NM , VD.VD_NM , VD.VD_NM_EN , VD.ERP_VD_CD , VD.BIZREGNO , VD.CORPNO , VD.CTRY_CCD , VD.TAX_ID , VDOG.OBD_TYP_CCD , VDOG.OORG_CD FROM VD VD INNER JOIN OORG_VD VDOG ON VDOG.TEN_ID = VD.TEN_ID AND VDOG.VD_CD = VD.VD_CD WHERE VD.TEN_ID = #{g.tenant} AND VDOG.OORG_CD = #{p.oorg_cd} AND VD.STS != 'D'
AND VD.ERP_VD_CD LIKE UPPER(#{vdCdPattern})
AND (UPPER(VD.VD_NM) LIKE UPPER(#{vdNmPattern}) OR UPPER(VD.VD_NM_EN) LIKE UPPER(#{vdNmPattern})) /* 협력사명 검색조건*/
AND VD.BIZREGNO LIKE UPPER(#{bizRegNoPattern})
AND VDOG.OBD_TYP_CCD = #{p.obd_typ_ccd}
ORDER BY VD.ERP_VD_CD

/** shared.findListSourcingGroupVendor */ SELECT VD.VD_CD, VD.ERP_VD_CD, CASE WHEN VD.ERP_VD_CD IS NULL THEN VD.VD_CD ELSE VD.ERP_VD_CD END AS DISP_VD_CD, CASE WHEN 'en_US' = #{g.locale} THEN VD.VD_NM_EN ELSE VD.VD_NM END AS DISP_VD_NM, VD.VD_NM, VD.VD_NM_EN, VD.BIZREGNO, VD.CTRY_CCD, VD.TAX_ID, VD.EU_VAT_REG_ID, DTL_ADDR_1, DTL_ADDR_2, OG.OORG_CD, OG.OBD_TYP_CCD, OG.OORG_INI_TRANSN_ST_DT, CH.VD_PIC_UUID, CH.VD_PIC_NM, CH.VD_PIC_EML, CH.VD_PIC_DEPT_NM, CH.VD_PIC_JOBTIT_NM, CH.VD_PIC_TEL, SG_VD.SG_CD, CASE WHEN 'en_US' = #{g.locale} THEN SG_NM_EN ELSE SG_NM END AS SG_NM FROM VD VD INNER JOIN OORG_VD OG ON OG.TEN_ID = VD.TEN_ID AND OG.VD_CD = VD.VD_CD INNER JOIN ( SELECT SG.TEN_ID , SG.SG_CD , SG.SG_NM , SG.SG_NM_EN , OG.VD_CD , OG.OORG_CD FROM SG SG INNER JOIN VMG VMG ON VMG.TEN_ID = SG.TEN_ID AND VMG.VMG_CD = SG.SG_CD INNER JOIN OORG_VD_VMG VD_VMG ON VD_VMG.TEN_ID = VMG.TEN_ID AND VD_VMG.VMG_UUID = VMG.VMG_UUID INNER JOIN OORG_VD OG ON OG.OORG_VD_UUID = VD_VMG.OORG_VD_UUID WHERE SG.TEN_ID = #{g.tenant} AND VD_VMG.SUPAVL_YN = 'Y'
AND SG.SG_CD IN
#{sg}
) SG_VD ON OG.TEN_ID = SG_VD.TEN_ID AND OG.VD_CD = SG_VD.VD_CD AND OG.OORG_CD = SG_VD.OORG_CD LEFT OUTER JOIN (SELECT CP.TEN_ID, CP.VD_CD, CP.VD_PIC_EML, CP.VD_PIC_UUID, CP.VD_PIC_NM, CP.VD_USR_ID, CP.VD_PIC_DEPT_NM, CP.VD_PIC_JOBTIT_NM, CP.VD_PIC_TEL FROM VD_PIC CP INNER JOIN TASK_VD_PIC CA ON CP.TEN_ID = CA.TEN_ID AND CP.VD_PIC_UUID = CA.VD_PIC_UUID WHERE CA.TASK_TYP_CCD = #{p.task_typ_ccd} ) CH ON OG.TEN_ID = CH.TEN_ID AND OG.VD_CD = CH.VD_CD WHERE VD.TEN_ID = #{g.tenant} AND OG.OORG_CD = #{p.oorg_cd} AND VD.STS != 'D'
AND VD.CTRY_CCD = #{p.ctry_ccd} /* 국가 코드 */
AND OG.OBD_TYP_CCD = #{p.obd_typ_ccd} /* 등록유형 거래유형*/
AND VD.ERP_VD_CD LIKE UPPER(#{p_erp_vd_cd})
AND (UPPER(VD.VD_NM) LIKE UPPER(#{p_vd_nm}) OR UPPER(VD.VD_NM_EN) LIKE UPPER(#{p_vd_nm})) /* 협력사명 검색조건*/
ORDER BY VD.VD_CD DESC, SG_VD.SG_CD

mapper.update:
/* shared.initPasswordByEmailAndUserId:  사용자 사용자 비밀번호 초기화  */
UPDATE USR
SET
    PWD = #{p.password}
  ,PWD_INIT_YN = 'Y'
  ,PWD_INPERR_CNT = 0
  ,ACCT_LCKD_YN = 'N'
  ,PWD_CHG_REQD_YN = 'Y'
  ,PWD_CHG_DTTM = #{g.now}
  ,MODR_ID = #{g.username}
  ,MOD_DTTM = #{g.now}
WHERE TEN_ID = #{p.ten_id}
  AND USR_ID = #{p.usr_id}
  AND EML = #{p.eml}
